INSERT INTO `tbl_news_content` (`id`, `title`, `alias`, `introtext`, `fulltext`, `catid`, `uid`, `thumbnail`, `ordering`, `created`, `metakey`, `metadesc`, `cdate`, `mdate`, `status`, `link_original`) VALUES
(1, 'Tỷ giá USD/VNĐ ngày 01/07/2015', 'Ty-gia-USD-VND-ngay-01-07-2015', 'Sáng 1/7, tỷ giá USD/VND tại nhiều NHTM giảm nhẹ khoảng từ 5 - 10 đồng cả hai chiều mua và bán.', '<span class="VB_gioi_thieu">Sáng 1/7, tỷ giá USD/VND tại nhiều NHTM giảm nhẹ khoảng từ 5 - 10 đồng cả hai chiều mua và bán.</span><br /> <div class="vbbox-selectcontent2"> <p> <img alt="http://a8.vietbao.vn/images/vn888/hot/v2014/d8fd9e4a59-1-ngoaite-1ddb5.jpeg" src="http://a8.vietbao.vn/images/vn888/hot/v2014/d8fd9e4a59-1-ngoaite-1ddb5.jpeg" /></p> <div> <p><span class="VB_noi_dung">Ngân hàng Vietcombank giảm 10 đồng tỷ giá USD/VND ở cả chiều mua và bán xuống 21.770 – 21.830 đồng/USD chiều mua vào- bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng BIDV và Vietinbank tiếp tục niêm yết tỷ giá USD/VND tại mức 21.780 – 21.840 đồng/USD chiều mua vào- bán ra, không đổi so với ngày hôm qua.</span></p> <p><span class="VB_noi_dung">Ngân hàng Techcombank niêm yết giao dịch USD/VND ở mức 21.760 đồng/USD mua vào và 21.850 đồng/USD bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng HSBC niêm yết tỷ giá USD/VND chiều mua vào - bán ra tại ngưỡng 21.750- 21.850 đồng/USD. GIảm 10 đồng cả hai chiều mua và bán so với ngày hôm qua.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng ACB và Eximbank cùng niêm yết tỷ giá USD/VND tại mức 21.760 - 21.840 đồng/USD (mua - bán). GIảm 5 đồng cả hai chiều mua và bán.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng Nhà nước Việt Nam công bố tỷ giá giao dịch bình quân trên thị trường ngoại tệ liên ngân hàng của đồng Việt Nam với USD áp dụng cho ngày 23/06/2015 như sau: 1 USD = 21.673 đồng Việt Nam.</span></p> <p><strong><span class="VB_noi_dung">15h30, trên thị trường Hà Nội, giá USD tự do giao dịch ở mức mua vào 21.820 đồng/USD và bán ra là 21.840 đồng/USD.</span></strong></p> </div> </div> <div class="author"><em>VietBao.vn (Tổng hợp)</em></div>', 3, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/d8fd9e4a59-1-ngoaite-1ddb5.jpeg', 0, '2015-07-01 15:39:38', 'Việt Nam,tỷ giá,ngân hàng,hai chiều,bán ra,cả hai,niêm yết,mua,đồng,ngày,giảm,10', 'Sáng 1/7, tỷ giá USD/VND tại nhiều NHTM giảm nhẹ khoảng từ 5 - 10 đồng cả hai chiều mua và bán. ', '2015-07-07 13:39:44', '2015-07-07 14:04:46', 1, 'http://vietbao.vn/Kinh-te/Ty-gia-USDVND-ngay-01072015/2147574122/90/'),
(2, 'Tỷ giá USD/VNĐ ngày 02/07/2015', 'Ty-gia-USD-VND-ngay-02-07-2015', 'Sáng 02/07, tỷ giá USD/VND giảm tại hầu hết các ngân hàng thương mại với mức giảm vào khoảng từ 5 - 10 đồng/USD.', '<span class="VB_gioi_thieu">Sáng 02/07, tỷ giá USD/VND giảm tại hầu hết các ngân hàng thương mại với mức giảm vào khoảng từ 5 - 10 đồng/USD.</span> <div class="vbbox-selectcontent2"> <p align="center"> <img alt="http://a8.vietbao.vn/images/vn888/hot/v2014/1ad40b05d7-1-tigia.jpeg" src="http://a8.vietbao.vn/images/vn888/hot/v2014/1ad40b05d7-1-tigia.jpeg" height="313" width="471" /></p> <div> <p><span class="VB_noi_dung">Ngân hàng Vietcombank niêm yết tỷ giá USD/VND tại mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra.Không đổi so với ngày hôm qua.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng BIDV niêm yết tỷ giá USD/VND tại mức 21.780 – 21.830 đồng/USD chiều mua vào- bán ra, giảm 10 đồng chiều bán ra so với ngày hôm qua.</span></p> <p><span class="VB_noi_dung">Ngân hàng Vietinbank niêm yết tỷ giá USD/VND tại mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra, giảm 10 đồng cả hai chiều mua và bán.<br /></span></p> <p><span class="VB_noi_dung">Cũng giảm 10 đồng cả hai chiều mua và bán là ngân hàng Techcombank với mức niêm yết giao dịch USD/VND ở mức 21.750 đồng/USD mua vào và 21.840 đồng/USD bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng HSBC niêm yết tỷ giá USD/VND chiều mua vào - bán ra tại ngưỡng 21.750- 21.850 đồng/USD.</span></p> <p><span class="VB_noi_dung">Ngân hàng ACB và Eximbank cùng niêm yết tỷ giá USD/VND tại mức 21.755 - 21.835 đồng/USD (mua - bán). Giảm 5 đồng cả hai chiều mua và bán.</span></p> <p><span class="VB_noi_dung">Ngân hàng Nhà nước Việt Nam công bố tỷ giá giao dịch bình quân trên thị trường ngoại tệ liên ngân hàng của đồng Việt Nam với USD áp dụng cho ngày 23/06/2015 như sau: 1 USD = 21.673 đồng Việt Nam.</span></p> <p><span class="VB_noi_dung"><strong>10h15, trong khi tỷ giá USD giảm tại các NHTM thì trên thị trường tự do Hà Nội, giá USD tự do giao dịch ở mức mua vào 21.830 đồng/USD và bán ra là 21.850 đồng/USD.</strong></span></p> </div> </div> <div class="author">VietBao.vn (Tổng hợp)</div>', 3, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/1ad40b05d7-1-tigia.jpeg', 0, '2015-07-02 10:19:14', 'Việt Nam,ngân hàng thương mại,tỷ giá,niêm yết,bán ra,giảm,mua,ngày,chiều,đồng', 'Sáng 02/07, tỷ giá USD/VND giảm tại hầu hết các ngân hàng thương mại với mức giảm vào khoảng từ 5 - 10 đồng/USD.', '2015-07-07 13:39:44', '2015-07-07 14:04:46', 1, 'http://vietbao.vn/Kinh-te/Ty-gia-USDVND-ngay-02072015/2147574440/90/'),
(3, 'Tỷ giá USD/VNĐ ngày 03/07/2015', 'Ty-gia-USD-VND-ngay-03-07-2015', 'Sáng 03/07, tỷ giá USD/VND tại các ngân hàng thương mại được duy trì ổn định sau một ngày điều chỉnh giảm nhẹ (02/07). Trên thị trường tự do giá USD vẫn tiếp tục đà điều chỉnh tăng so với ngày hôm qua.', '<div> <div class="vbbox-selectcontent2"> <div class="vbbox-selectcontent2"> <div><span class="VB_noi_dung"><span class="VB_gioi_thieu">Sáng 03/07, tỷ giá USD/VND tại các ngân hàng thương mại được duy trì ổn định sau một ngày điều chỉnh giảm nhẹ (02/07). Trên thị trường tự do giá USD vẫn tiếp tục đà điều chỉnh tăng so với ngày hôm qua.</span></span></div> </div> </div> </div> <br /> <div class="vbbox-selectcontent2"> <div class="vbbox-selectcontent2"></div> <p><span class="VB_noi_dung"> <img class="shrinkToFit img-share-slide img-share-slide-0" src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_cda0248613-1-hoi10-31-59-00000013-48-03-00000012-17-12-000000.jpeg" height="336" width="444" alt="Ty gia USD/VND ngay 03/07/2015" /></span></p> <div> <p><span class="VB_noi_dung">Ngân hàng Vietcombank tỷ giá USD/VND niêm yết ở mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra, không đổi so với ngày 02/07.</span></p> <p><span class="VB_noi_dung">Ngân hàng BIDV niêm yết tỷ giá USD/VND tại mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra, giảm 10 đồng chiều mua vào so với ngày hôm qua.</span></p> <div class="vbbox-selectcontent2"> <div><span class="VB_noi_dung">Ngân hàng Vietinbank hiện cũng niêm yết tỷ giá USD/VND tại mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra.<br /></span></div> </div> <p><span class="VB_noi_dung">Ngân hàng Techcombank niêm yết giao dịch USD/VND ở mức 21.750 đồng/USD mua vào và 21.840 đồng/USD bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng HSBC niêm yết tỷ giá USD/VND chiều mua vào - bán ra tại ngưỡng 21.750- 21.850 đồng/USD, không đổi so với ngày hôm qua.</span></p> <div class="vbbox-selectcontent2"> <div><span class="VB_noi_dung">Ngân hàng ACB và Eximbank cùng tiếp tục niêm yết tỷ giá USD/VND tại mức 21.755 - 21.835 đồng/USD (mua - bán).<br /></span></div> </div> <p><span class="VB_noi_dung">Ngân hàng Nhà nước Việt Nam công bố tỷ giá giao dịch bình quân trên thị trường ngoại tệ liên ngân hàng của đồng Việt Nam với USD áp dụng cho ngày 03/07/2015 như sau: 1 USD = 21.673 đồng Việt Nam.</span></p> <p><span class="VB_noi_dung"><em><strong>9h00, trên thị trường Hà Nội, giá USD tự do giao dịch ở mức mua vào 21.840 đồng/USD và bán ra là 21.870 đồng/USD, tăng 10 đồng chiều mua vào và 20 đồng chiều bán ra so với ngày hôm qua (02/07).</strong></em><br /></span></p> </div> </div> <br /> <div class="vbbox-selectcontent2"></div> <div class="author" align="right"><em><span class="VB_noi_dung">VietBao.vn (Tổng hợp)</span></em></div>', 3, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/best_cda0248613-1-hoi10-31-59-00000013-48-03-00000012-17-12-000000.jpeg', 0, '2015-07-03 08:57:11', 'Việt Nam,thị trường tự do,ngân hàng thương mại,ngày hôm qua,tỷ giá,bán ra,so với,điều chỉnh,niêm yết,mua vào,chiều,USD,đồng', 'Sáng 03/07, tỷ giá USD/VND tại các ngân hàng thương mại được duy trì ổn định sau một ngày điều chỉnh giảm nhẹ (02/07).', '2015-07-07 13:39:44', '2015-07-07 14:04:46', 1, 'http://vietbao.vn/Kinh-te/Ty-gia-USDVND-ngay-03072015/2147574614/90/'),
(4, 'Tỷ giá USD/VNĐ ngày 06/07/2015', 'Ty-gia-USD-VND-ngay-06-07-2015', 'Sáng đầu tuần (ngày 06/07), hầu hết các ngân hàng thương mại đều giữ ổn định tỷ giá USD/VND. Trên thị trường tự do Hà Nội, tỷ giá USD tiếp tục tăng thêm 10 đồng so với cuối tuần trước (ngày 3/7).', '<div class="vbbox-selectcontent2"><span class="VB_gioi_thieu">Sáng đầu tuần (ngày 06/07), hầu hết các ngân hàng thương mại đều giữ ổn định tỷ giá USD/VND. Trên thị trường tự do Hà Nội, tỷ giá USD tiếp tục tăng thêm 10 đồng so với cuối tuần trước (ngày 3/7).</span><br /> <p> <img alt="http://a8.vietbao.vn/images/vn888/hot/v2014/c26e44acf6-1-8Achot-ae747.jpeg" src="http://a8.vietbao.vn/images/vn888/hot/v2014/c26e44acf6-1-8Achot-ae747.jpeg" width="483" height="329" /></p> <div> <p><span class="VB_noi_dung">Ngân hàng Vietcombank tỷ giá USD/VND niêm yết ở mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra, không đổi so với ngày 03/07.</span></p> <p><span class="VB_noi_dung">Ngân hàng BIDV niêm yết tỷ giá USD/VND tại mức 21.775 – 21.835 đồng/USD chiều mua vào- bán ra, tăng 5 đồng cả hai chiều mua và bán so với ngày 03/07.</span></p> <p><span class="VB_noi_dung">Ngân hàng ACB và Eximbank cùng tiếp tục niêm yết tỷ giá USD/VND tại mức 21.755 - 21.835 đồng/USD (mua - bán). <br /></span></p> <p><span class="VB_noi_dung">Ngân hàng Vietinbank hiện cũng niêm yết tỷ giá USD/VND tại mức 21.770 – 21.830 đồng/USD chiều mua vào- bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng Techcombank niêm yết giao dịch USD/VND ở mức 21.750 đồng/USD mua vào và 21.840 đồng/USD bán ra.</span></p> <p><span class="VB_noi_dung">Ngân hàng HSBC niêm yết tỷ giá USD/VND chiều mua vào - bán ra tại ngưỡng 21.750- 21.850 đồng/USD.</span></p> <p><span class="VB_noi_dung">Ngân hàng Nhà nước Việt Nam công bố tỷ giá giao dịch bình quân trên thị trường ngoại tệ liên ngân hàng của đồng Việt Nam với USD áp dụng cho ngày 03/07/2015 như sau: 1 USD = 21.673 đồng Việt Nam.</span></p> <div> <p><span class="VB_noi_dung"><strong>9h00, trên thị trường Hà Nội, giá USD tự do tiếp tục tăng thêm 10 đồng cả hai chiều mua và bán, giao dịch ở mức mua vào 21.850 đồng/USD và bán ra là 21.880 đồng/USD.</strong></span></p> <p><span class="VB_noi_dung"><strong>15h15 chiều nay, tỷ giá USD tự do tại thị trường Hà Nội tiếp tục giao dịch tại khoảng giá 21.840 - 21.850 đồng/USD mua vào, 21.870 - 21.880 đồng/USD bán ra.</strong></span></p> </div> </div> </div> <div class="author"><em>VietBao.vn (Tổng hợp)</em></div>', 3, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/c26e44acf6-1-8Achot-ae747.jpeg', 0, '2015-07-06 15:14:19', 'Hà Nội,Việt Nam,thị trường tự do,ngân hàng thương mại,định tỷ giá,giữ ổn định,bán ra,niêm yết,tiếp tục,so với,tăng thêm,ngày,mua,đồng,USD', 'Sáng đầu tuần (ngày 06/07), hầu hết các ngân hàng thương mại đều giữ ổn định tỷ giá USD/VND. Trên thị trường tự do Hà Nội, tỷ giá USD tiếp tục tăng thêm 10 đồng so với cuối tuần trước (ngày 3/7). ', '2015-07-07 13:39:44', '2015-07-07 14:04:46', 1, 'http://vietbao.vn/Kinh-te/Ty-gia-USDVND-ngay-06072015/2147575124/90/'),
(5, 'Tỷ giá USD/VNĐ ngày 07/07/2015', 'Ty-gia-USD-VND-ngay-07-07-2015', 'Sáng 07/07, tỷ giá USD/VND tại các ngân hàng thương mại biến động nhẹ trong khi trên thị trường USD tự do, tỷ giá khá ổn định và gần như không thay đổi so với ngày hôm qua.', '<div><span class="VB_gioi_thieu">Sáng 07/07, tỷ giá USD/VND tại các ngân hàng thương mại biến động nhẹ trong khi trên thị trường USD tự do, tỷ giá khá ổn định và gần như không thay đổi so với ngày hôm qua.<br /></span></div> <div class="vbbox-selectcontent2"> <p align="center"> <img alt="http://a8.vietbao.vn/images/vn888/hot/v2014/85acc954cd-1-1-chots.jpeg" src="http://a8.vietbao.vn/images/vn888/hot/v2014/85acc954cd-1-1-chots.jpeg" width="438" height="328" /></p> <div> <p><span class="VB_noi_dung">Ngân hàng Vietcombank và Vietinbank cùng niêm yết tỷ giá USD/VND ở mức 21.775 – 21.835 đồng/USD chiều mua vào- bán ra, tăng nhẹ 5 đồng cả hai chiều mua và bán so với sáng hôm qua.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng BIDV niêm yết tỷ giá USD/VND tại mức 21.775 – 21.835 đồng/USD chiều mua vào- bán ra, không đổi so với ngày hôm qua.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng Eximbank niêm yết tỷ giá USD/VND tại mức 21.775 - 21.835 đồng/USD (mua - bán).Tăng 20 đồng chiều mua vào.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng Techcombank niêm yết giao dịch USD/VND ở mức 21.755 đồng/USD mua vào và 21.845 đồng/USD bán ra, tăng 5 đồng cả hai chiều mua và bán so với sáng hôm qua.<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng HSBC niêm yết tỷ giá USD/VND chiều mua vào - bán ra tại ngưỡng 21.750- 21.850 đồng/USD.</span></p> <p><span class="VB_noi_dung">Đông Á Bank niêm yết tỷ giá USD/VND tại 21.750 - 21.840 đồng/USD (mua vào - bán ra)<br /></span></p> <p><span class="VB_noi_dung">Ngân hàng Nhà nước Việt Nam công bố tỷ giá giao dịch bình quân trên thị trường ngoại tệ liên ngân hàng của đồng Việt Nam với USD áp dụng cho ngày 03/07/2015 như sau: 1 USD = 21.673 đồng Việt Nam.</span></p> <p><span class="VB_noi_dung"><strong>9h00, trên thị trường Hà Nội, giá USD tự do giao dịch ở mức mua vào 21.850 đồng/USD và bán ra là 21.880 đồng/USD, không đổi so với ngày hôm qua.</strong></span></p> </div> </div> <div class="author">VietBao.vn (Tổng hợp)</div>', 3, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/85acc954cd-1-1-chots.jpeg', 0, '2015-07-07 08:54:27', 'Việt Nam,ngân hàng thương mại,ngày hôm qua,không thay đổi,tỷ giá,so với,niêm yết,bán ra,mua vào,thị trường,USD,chiều,đồng,sáng', 'Sáng 07/07, tỷ giá USD/VND tại các ngân hàng thương mại biến động nhẹ trong khi trên thị trường USD tự do, tỷ giá khá ổn định và gần như không thay đổi so với ngày hôm qua. ', '2015-07-07 13:39:44', '2015-07-07 14:04:46', 1, 'http://vietbao.vn/Kinh-te/Ty-gia-USDVND-ngay-07072015/2147575427/90/'),
(6, 'SSI tiếp tục độc chiếm ngôi vương thị phần môi giới trên HNX', 'SSI-tiep-tuc-doc-chiem-ngoi-vuong-thi-phan-moi-gioi-tren-HNX', 'CTCP Chứng khoán Sài Gòn (SSI) tiếp tục giữ vững vị trí số 1 thị phần môi giới trên sàn cả hai sàn HOSE và HNX.', '<b>CTCP Chứng khoán Sài Gòn (SSI) tiếp tục giữ vững vị trí số 1 thị phần môi giới trên sàn cả hai sàn HOSE và HNX.</b> <p>Sở GDCK Hà Nội thông báo thị phần môi giới cổ phiếu niêm yết lớn nhất trên HNX trong quý I/2015.</p> <p>Theo đó, CTCP Chứng khoán Sài Gòn (SSI) tiếp tục giữ vững vị trí số 1 với 8,54%, trước đó, trong quý I/2015, lần đầu tiên trong lịch sử SSI công phá thành công ngôi vương trên sàn Hà Nội.</p> <p>Tiếp theo bảng xếp hạng này, VNDS đã vươn lên vị trí thứ hai với 7,095%, trước đó, quý I/2015, VNDS chỉ xếp ở vị trí thứ 5. Trong khi đó, HSC vẫn duy trì vị trí thứ 3 với 6,575%. Đáng chú ý, SHS từ vị trí thứ hai ở quý I đã bị đẩy xuống vị trí thứ 4 với 6,232%.</p> <p>Trong khi đó, MBS leo từ vị trí thứ 7 lên đứng thứ 5 với 5,64%. BSC và BVSC đều tụt xuống một bậc và đứng ở vị trí thứ 6 và 7, với thị phần lần lượt là 5,082% và 5,018%. Ba vị trí còn lại thuộc về FPTS, VCSC và ACBS với 4,296%, 3,995% và 3,981%.</p> <p><img src="http://a9.vietbao.vn/images/vn999/196/2015/07/20150706-ssi-tiep-tuc-doc-chiem-ng-244-i-vuong-thi-phan-m-244-i-gioi-tr-234-n-hnx-1.jpg" alt="SSI tiep tuc doc chiem ngoi vuong thi phan moi gioi tren HNX" /></p> <p>Tính chung 10 CTCK có thị phần môi giới lớn nhất sàn HNX chiếm 56,45% thị phần cả nước, các CTCK còn lại "chia sẻ" 43,55% thị phần. Trong đó top 3 CTCK có thị phần lớn nhất là SSI, VNDS, HSC chiếm 22,21% thị phần cả nước.</p> <p>Trên thị trường UPCoM, VCSC đã giành vị trí quán quân với thị phần 16,053%. Vị trí thứ hai và ba thuộc về SSI và SHS, với thị phần lần lượt là 8,985% và 8,588%.</p> <p><img src="http://a9.vietbao.vn/images/vn999/196/2015/07/20150706-ssi-tiep-tuc-doc-chiem-ng-244-i-vuong-thi-phan-m-244-i-gioi-tr-234-n-hnx-2.jpg" alt="SSI tiep tuc doc chiem ngoi vuong thi phan moi gioi tren HNX" /></p> <p><em>Thị phần môi giới sàn Upcom</em></p> <p>Trước đó, Sở GDCK TP.HCM cũng đã công bố thị phần giao dịch môi giới quý II/2015. Trong đó, top 10 CTCK có thị phần môi giới cổ phiếu và chứng chỉ quỹ trên sàn HOSE vẫn là những cái tên quen thuộc như quý I/2015. Đáng chú ý, top 3 đứng đầu không có sự thay đổi, theo thứ tự SSI vẫn đứng đầu với 13,52% , HSC 11,24% và VCSC 7,99%.</p> <p><em>10 CTCK có thị phần Môi giới lớn nhất Q2/2015 tại HNX</em></p> <div class="vivamain196" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("196"); //]]> </script></div>', 4, 0, 'http://img.vietbao.vn/images/2000/vn999/196/2015/07/20150706-ssi-tiep-tuc-doc-chiem-ng-244-i-vuong-thi-phan-m-244-i-gioi-tr-234-n-hnx-1.jpg', 0, '2015-07-06 23:58:00', 'Sài Gòn,CTCP Chứng,thị phần,môi giới,vị trí,tiếp tục,độc chiếm,Số 1,SSI,sàn,thứ,HNX,vương', 'CTCP Chứng khoán Sài Gòn (SSI) tiếp tục giữ vững vị trí số 1 thị phần môi giới trên sàn cả hai sàn HOSE và HNX.', '2015-07-07 13:42:07', '2015-07-07 14:04:48', 1, 'http://vietbao.vn/Kinh-te/SSI-tiep-tuc-doc-chiem-ngoi-vuong-thi-phan-moi-gioi-tren-HNX/196027713/91/'),
(7, 'Thành viên hội đồng quản trị độc lập, khó độc lập', 'Thanh-vien-hoi-dong-quan-tri-doc-lap-kho-doc-lap', 'Với vai trò chất vấn, giám sát, kiểm soát công tác quản lý công ty, thành viên HĐQT độc lập góp phần bảo vệ lợi ích chính đáng của cổ đông, nhất là những cổ đông nhỏ. Tuy nhiên, thực tế cho thấy, vẫn còn nhiều doanh nghiệp niêm yết (DNNY) chưa tuân thủ qu', '<div><span class="VB_gioi_thieu">Với vai trò chất vấn, giám sát, kiểm soát công tác quản lý công ty, thành viên HĐQT độc lập góp phần bảo vệ lợi ích chính đáng của cổ đông, nhất là những cổ đông nhỏ. Tuy nhiên, thực tế cho thấy, vẫn còn nhiều doanh nghiệp niêm yết (DNNY) chưa tuân thủ quy định về thành viên HĐQT độc lập, nếu đáp ứng đủ số lượng thì tính độc lập cũng chưa rõ nét.</span> <p align="center"><span class="VB_noi_dung"><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150704-thanh-vien-hoi-dong-quan-tri-doc-lap-kho-doc-lap-1.jpg" alt="Thanh vien hoi dong quan tri doc lap, kho doc lap" height="256" width="449" /></span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">DN đáp ứng quy định: còn thiểu số</span></span></p> <p>Theo quy định hiện hành, HĐQT của công ty đại chúng phải có 1/3 số thành viên là thành viên độc lập. Thống kê của Sở GDCK TP. HCM (HOSE), toàn bộ các DNNY trên Sở đều đáp ứng được quy định có tối thiểu 1/3 số thành viên HĐQT là thành viên HĐQT không điều hành.</p> <p>Tuy nhiên, chỉ có khoảng 1/3 số DN đáp ứng được quy định về thành viên HĐQT độc lập tại Thông tư 121/2012 của Bộ Tài chính về quản trị công ty áp dụng cho các công ty đại chúng (không phải là cổ đông lớn hoặc người đại diện của cổ đông lớn; không tham gia điều hành, hoàn toàn không có liên hệ với công ty con, công ty liên kết; không phải là các bên có quyền lợi liên quan, không tham gia công ty kiểm toán, tư vấn công ty 2 năm gần nhất…).</p> <p>Ngay cả các DNNY có quy mô lớn, tỷ lệ thành viên HĐQT độc lập cũng chưa đảm bảo tỷ lệ 1/3 theo quy định. Nhìn vào cơ cấu HĐQT CTCP Sữa Việt Nam (VNM), có 7 thành viên, chỉ có 1 thành viên HĐQT độc lập là bà Lê Thị Băng Tâm, Chủ tịch HĐQT HDBank. Hay tại FPT, HĐQT có 9 thành viên, trong đó có 3 thành viên không tham gia điều hành và có 2 thành viên quốc tế tham gia HĐQT độc lập là ông Tomokazu Hamaguchi, Chủ tịch Hiệp hội Dịch vụ công nghệ thông tin Nhật Bản (JISA) và ông Dan E Khoo, Chủ tịch danh dự của Liên minh Công nghệ thông tin Thế giới (WITSA).</p> <p>Tương tự, CTCP Cơ điện lạnh (REE) HĐQT trị hiện tại bao gồm 5 thành viên; trong đó có 2 thành viên điều hành, 2 thành viên không điều hành và 1 thành viên độc lập là ông Đặng Hồng Tân - hiện không sở hữu bất cứ cổ phiếu nào của REE.</p> <p>CTCP Khoan và dịch vụ Dầu khí (PVD) là DN hiếm hoi trên sàn mà HĐQT gồm 7 thành viên thì có tới 3 thành viên độc lập; trong đó có ông Lê Văn Bé, là Chủ tịch HĐQT MBCapital, ông Dương Xuân Quang là Phó tổng giám đốc PVcomBank.</p> <p>Hiện vẫn còn nhiều DNNY chưa có thành viên HĐQT độc lập. CTCP Khí Việt Nam (GAS), DN có quy mô vốn lớn nhất trên sàn hiện có 6 thành viên trong HĐQT, nhưng không có thành viên HĐQT độc lập. Tương tự, CTCP Gemadept (GMD), CTCP Thủy sản Hùng Vương (HVG), CTCP Thiết bị y tế Việt - Nhật (JVC)… và nhiều DNNY khác cũng không có thành viên HĐQT độc lập.</p> <p><span class="VB_tieu_de_phu">Thành viên độc lập, có độc lập?</span></p> <p>Có những DN có thành viên HĐQT độc lập, nhưng có độc lập thực sự hay không lại là câu chuyện khác. Chẳng hạn, tại ĐHCĐ năm 2013, CTCP Giống cây trồng Trung ương (NSC), 3 nhóm cổ đông nắm giữ 56% vốn đã đề cử 1 thành viên HĐQT độc lập là bà Nguyễn Trà My vào vị trí thành viên HĐQT độc lập. Đến nay, bà My vẫn đang là thành viên HĐQT độc lập của NSC. </p> <p>Đánh giá về tình hình quản trị công ty của DN Việt, ông Vũ Hữu Điền, Giám đốc Đầu tư Dragon Capital cho rằng, ảnh hưởng của cổ đông lớn đến cơ chế hoạt động, quyền quản trị công ty là rất lớn.</p> <p>Nhiều trường hợp, HĐQT thành lập theo quy định, nhưng người có quyền đưa ra các quyết sách thực sự cho DN lại không “ngồi ghế” HĐQT.</p> <p>Ngoài ra, theo quy định, một trong những tiêu chuẩn là thành viên HĐQT độc lập không phải là cổ đông lớn. Tuy nhiên, quy định cổ đông lớn là cổ đông sở hữu từ 5% vốn cổ phần là tỷ lệ quá lớn, chỉ cần cổ đông sở hữu 4,99% là “lách” được quy định này. </p> <p>Chia sẻ tại một buổi hội thảo, đại diện một công ty niêm yết cho rằng, nên có quy định cổ đông lớn không được ứng cử, đề cử vị trí thành viên HĐQT độc lập, bởi nếu không sẽ làm giảm tính độc lập của thành viên này.</p> <p>Vị này cũng chia sẻ, có thể quy định thêm về cơ cấu, thành phần của HĐQT, tức ngoài quy định tối thiểu 1/3 thành viên HĐTQ độc lập thì nên có quy định khuyến khích DN có một HĐQT đại diện cho nhiều hình thức sở hữu (Nhà nước, cổ đông lớn, nước ngoài, quỹ đầu tư….) sẽ có đóng góp lớn hơn cho doanh nghiệp. Bởi nếu cơ cấu HĐQT cô đặc, chủ yếu là các thành viên đại diện một nhóm cổ đông lớn sẽ gây ảnh hưởng tới các quyết định của ban điều hành, kiểm soát hoạt động công ty theo lợi ích của nhóm cổ đông nhất định.</p> <p>Cùng quan điểm này, ông Điền cho rằng, việc cho cổ đông nhỏ đề cử HĐQT độc lập là giải pháp hay.</p> <p>Việc có thành viên HĐQT độc lập là thông lệ tốt cho doanh nghiệp, cổ đông. Các doanh nghiệp Việt Nam có thể học hỏi kinh nghiệm của các nước trên thế giới bằng cách thuê tư vấn, kiểm toán bên ngoài hàng năm đánh giá tính độc lập của thành viên HĐQT trên những khía cạnh chủ yếu; có quy định thời hạn cho việc tham gia HĐQT với tư cách là thành viên độc lập. Nhưng quan trọng hơn cả, bản thân mỗi thành viên HĐQT độc lập cần xác định rõ vai trò, trách nhiệm và nghĩa vụ của mình là đại diện và bảo vệ cổ đông. </p> </div> <br /> <div class="vivamain199" align="right"><span class="VB_noi_dung">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></span></div>', 4, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150704-thanh-vien-hoi-dong-quan-tri-doc-lap-kho-doc-lap-1.jpg', 0, '2015-07-04 07:56:07', 'thành viên hội đồng quản trị,Quản trị công ty,quản lý công ty,bảo vệ lợi ích,cổ đông lớn,tính độc lập,theo quy định,cổ đông nhỏ,lập là,điều hành,doanh nghiệp,đáp ứng,đại diện,tuy nhiên,CTCP', 'Với vai trò chất vấn, giám sát, kiểm soát công tác quản lý công ty, thành viên HĐQT độc lập góp phần bảo vệ lợi ích chính đáng của cổ đông, nhất là những cổ đông nhỏ. Tuy nhiên, thực tế cho thấy, vẫn còn nhiều doanh nghiệp niêm yết (DNNY) chưa tuân thủ qu', '2015-07-07 13:42:07', '2015-07-07 14:04:48', 1, 'http://vietbao.vn/Kinh-te/Thanh-vien-hoi-dong-quan-tri-doc-lap-kho-doc-lap/199125100/91/'),
(8, 'Nhận định thị trường ngày 6/7: Rủi ro gia tăng', 'Nhan-dinh-thi-truong-ngay-6-7-Rui-ro-gia-tang', 'Xu thế tăng điểm có thể còn tiếp diễn. Dù vậy, thị trường bắt đầu phát đi tín hiệu tăng nóng. Thị trường có thể rung lắc khi VN-Index đang tiếp cận vùng kháng cự mạnh tiếp theo 620-625.', '<b>Xu thế tăng điểm có thể còn tiếp diễn. Dù vậy, thị trường bắt đầu phát đi tín hiệu tăng nóng. Thị trường có thể rung lắc khi VN-Index đang tiếp cận vùng kháng cự mạnh tiếp theo 620-625.</b><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150705-nhan-dinh-thi-truong-ngay-6-7-rui-ro-gia-tang-1.jpg" alt="Nhan dinh thi truong ngay 6/7: Rui ro gia tang" /> <p>ĐTCK lược trích báo cáo nhận định thị trường của một số CTCK cho phiên giao dịch ngày 6/7.</p> <p><em><strong>Rủi ro điều chỉnh sẽ xuất hiện</strong></em></p> <p><em>(CTCK Sài Gòn Hà Nội - SHS)</em></p> <p>Sau liên tiếp các phiên dao động tích lũy trong biên độ hẹp, thị trường bất ngờ có tuần bật tăng điểm tích cực nhờ dòng vốn giao dịch mạnh mẽ tại nhóm các cổ phiếu Bluechips, đặc biệt là sự mạnh lên bất ngờ của nhóm các cổ phiếu ngân hàng. Đà tăng áp đảo diễn ra trong 2 phiên giao dịch cuối tuần, sau hàng loạt các phiên dòng vốn phân bổ theo hướng tập trung vào các cổ phiếu mạnh riêng lẻ, khiến xu hướng tăng của VN-Index có phần chững lại, cho thấy về mặt cảm tính tâm lý thị trường đang ở trạng thái rất tích cực.</p> <p>Trên thực tế, xu hướng tích cực của dòng tiền đang được nuôi dưỡng bởi kỳ vọng Chính phủ sẽ sớm cho phép các ngân hàng mở room trong thời gian tới, giúp dòng vốn khối ngoại tiếp tục chảy mạnh mẽ vào nhóm các cổ phiếu này. Tuy vậy, chúng tôi đánh giá khả năng xảy ra của kỳ vọng này là khá thấp do ngành ngân hàng mang tính đặc thù nhạy cảm.</p> <p>Khối ngoại trong tuần qua tiếp tục xu hướng mua ròng khá tích cực trên cả hai sàn. Tuy vậy, xu hướng này có yếu đi trong các phiên cuối tuần. Chúng tôi cũng lưu ý những diễn biến khá phức tạp tại Hy Lạp vẫn chưa có hồi kết. Trong một động thái mới nhất, Thủ tướng Hy Lạp đã hối thúc người dân bỏ phiếu chống đối với đề xuất cứu trợ tài chính quốc tế trong cuộc trưng cầu diễn ra ngày 5/7 tới đây. Điều này khiến những lo ngại về tác động tiêu cực đối với vấn đề khủng hoảng nợ tại Hy Lạp tiếp tục tăng cao.</p> <p>Thị trường đang có dấu hiệu tăng nóng trong ngắn hạn từ hiệu ứng đẩy giá mạnh mẽ tại nhóm cổ phiếu ngân hàng và các mã Bluechips nhờ kỳ vọng nới room. Tuy vậy, sức tăng không nhận được sự hỗ trợ đồng đều của hầu hết các nhóm ngành khác cho thấy, thị trường vẫn chưa có sự đồng thuận tích cực giữa các nhóm ngành. Điều này khiến đà tăng của thị trường phụ thuộc rất lớn vào một vài nhóm ngành riêng lẻ. Rủi ro điều chỉnh do vậy sẽ xuất hiện khi áp lực chốt lời ngắn hạn diễn ra, đặc biệt trong bối cảnh kỳ vọng nới room sẽ chưa sớm được hiện thực hóa trong ngắn hạn. Nhà đầu tư nên thận trọng nếu vẫn đang nắm giữ tiền, không nên tiến hành mua đuổi trong các phiên tăng điểm.</p> <p><em><strong>Chỉ số đang hướng vào khu vực có kháng cự mạnh</strong></em></p> <p><em>(CTCK FPT - FPTS) </em></p> <p>Chỉ số đang hướng vào khu vực có kháng cự mạnh, trong khi các chỉ báo về xung lực đang dần đạt ngưỡng “quá mua” là cảnh báo cho các hoạt động giải ngân mới. </p> <p>Như đã đề cập, tín hiệu cải thiện dần của dòng tiền trong các phiên tới sẽ giúp giảm thiểu những lo ngại về khả năng kém bền vững của xu hướng. Theo đó, kỳ vọng dòng tiền sẽ lan tỏa mạnh hơn sang các nhóm cổ phiếu đã xuất hiện các khoảng giá hấp dẫn. Đây là yếu tố cốt lõi giúp thị trường duy trì những nhịp tăng giá bền vững trong ngắn hạn và là cơ sở chính cho các quyết định giải ngân mới.</p> <p>Ngoài ra, hành động giảm tỷ trọng dần đối với các nhóm cổ phiếu đã có sự hội tụ lớn của dòng tiền gần đây là cần thiết, đặc biệt là khi đã có các nhịp tăng giá mạnh. Nhóm cổ phiếu đáng chú ý trong thời gian tới sẽ tập trung vào nhóm có nền tảng cơ bản tốt và được dự báo tích cực về kết quả kinh doanh quý II.</p> <p><em><strong>Rủi ro điều chỉnh đang tiềm ẩn khá cao</strong></em></p> <p><em>(CTCK Bảo Việt - BVSC)</em></p> <p>Hiện tượng phân hóa đang khiến nhiều nhà đầu tư cá nhân mất phương hướng, 2 chỉ số tăng điểm mạnh, tuy nhiên sự lan tỏa của dòng tiền khá yếu, độ rộng thị trường ở mức tương đối cân bằng, trong khi khối lượng và giá trị giao dịch sụt giảm. Đây là những tín hiệu không thực sự</p> <p>tích cực về mặt kỹ thuật trong ngắn hạn. Mặc dù vậy, lực cầu bền bỉ của khối ngoại, sau khi Nghị định 60 được thông qua, tập trung ở các mã bluechips có sức ảnh hưởng lớn đến chỉ số vẫn đang đủ sức giữ nhịp cho thị trường. Trong bối cảnh dòng tiền nội còn giữ tâm lý khá thận trọng, biến động thị trường trong thời gian tới sẽ phụ thuộc lớn vào nhóm cổ phiếu dẫn dắt cùng với động thái mua bán ròng của khối ngoại.</p> <p>Mặc dù triển vọng tăng điểm của thị trường trong trung-dài hạn vẫn đang ở mức tích cực, nhưng vùng điểm hiện tại của 2 chỉ số đang tiềm ẩn rủi ro điều chỉnh khá cao trong ngắn hạn. Bên cạnh việc nắm giữ các mã cổ phiếu cơ bản cho danh mục trung-dài hạn, nhà đầu tư được khuyến nghị đẩy mạnh các hoạt động trading và kết hợp với các mục đích tái cơ cấu, chốt lời các vị thế ngắn hạn và chờ mua lại khi thị trường điều chỉnh sau đó.</p> <p><em><strong>Sẽ tăng điểm </strong></em></p> <p><em>(CTCK Maritime Bank – MSBS)</em></p> <p>Thị trường có tuần giao dịch đầy hứng khởi, VN-Index tiếp tục xu hướng tăng điểm trong phiên cuối tuần, tăng 10,73 điểm, dừng ở mức 616,43 điểm - mức cao nhất kể từ tháng 10 năm trước. Dòng tiền vào đã được cải thiện với gần 3.200 tỷ đồng toàn thị trường, cụm nến ngày đã phá vỡ dải Bollinger phía trên, chỉ báo kỹ thuật như MACD vẫn cho thấy xu hướng tích cực.</p> <p>Thị trường có thể sẽ tiếp tục tăng điểm trong 1, 2 phiên đầu tuần và điều chỉnh ở các phiên tiếp theo khi RSI tiến tới vùng quá mua, áp lực chốt lời gia tăng, tuy nhiên, nhìn chung thị trường sẽ vẫn giữ xu hướng tăng.</p> <p>Đầu tuần, thị trường sẽ tăng điểm với sự dẫn dắt của nhóm các cổ phiếu Bluechips và nhóm cổ phiếu ngân hàng. Nhà đầu tư chỉ nên ưu tiên nắm giữ cổ phiếu thực sự tốt, một số cổ phiếu chú ý như: TCM, SSI,…</p> <p><em><strong>Bắt đầu phát đi tín hiệu tăng nóng</strong></em></p> <p><em>(CTCP Chứng khoán KIS Việt Nam - KIS)</em></p> <p>Chúng tôi nhận thấy tâm lý của nhà đầu tư nội đã trở nên tốt hơn rất nhiều sau khi VN-Index vượt qua ngưỡng 600 ngày 3/7, cộng với động thái mua ròng được duy trì bởi khối ngoại. Theo đó, dòng tiền tham gia thị trường từ nhà đầu tư nội cũng đã cải thiện đáng kể. Rõ ràng, các yếu tố như nới room và TPA đang đóng vai trò hỗ trợ cho xu thế tăng điểm hiện nay, dù thị trường còn phân hóa khá rõ và bluechip vẫn đang dẫn dắt.</p> <p>Nói chung, xu thế tăng điểm có thể còn tiếp diễn. Dù vậy, thị trường bắt đầu phát đi tín hiệu tăng nóng. Chúng tôi hơi lo ngại thị trường có thể rung lắc khi VN-Index đang tiếp cận vùng kháng cự mạnh tiếp theo 620-625. Do đó, việc mua đuổi giá cao ở các mã đã tăng mạnh vào lúc này trở nên rủi ro hơn.</p> <p><em><strong>Xu hướng chủ đạo hiện nay vẫn là tăng</strong></em></p> <p><em>(CTCK Maybank KimEng - MBKE) </em></p> <p>Một lần nữa lưu ý xu hướng chủ đạo hiện nay của thị trường tiếp tục là tăng. Nhà đầu tư cần tiếp tục duy trì tỷ trọng cổ phiếu cao hơn tiền mặt và cố gắng điều chỉnh danh mục của mình theo hướng chú ý của dòng tiền để đạt được hiệu quả cao nhất.</p> <p><em><strong>Có thể tăng tỷ trọng cổ phiếu </strong></em></p> <p><em>(CTCK Rồng Việt -VDSC)</em></p> <p>Thị trường biến động tích cực hơn cùng với dòng tiền cũng tăng dần trở lại là những dấu hiệu tốt để thị trường tiếp tục duy trì xu hướng tăng trung hạn. Diễn biến này phù hợp cho nhà đầu tư cơ cấu lại danh mục và có thể tăng tỷ trọng cổ phiếu trong danh mục đầu tư.</p> <p><b>Ng.Tùng</b></p> <div class="vivamain199" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></div>', 4, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150705-nhan-dinh-thi-truong-ngay-6-7-rui-ro-gia-tang-1.jpg', 0, '2015-07-05 18:50:00', 'Hy Lạp,nhà đầu tư,xu hướng tăng,có thể rung,của thị trường,cổ phiếu,tăng điểm,tích cực,rủi ro,dòng tiền,tiếp tục,mạnh,nhóm,động,ngày', 'Xu thế tăng điểm có thể còn tiếp diễn. Dù vậy, thị trường bắt đầu phát đi tín hiệu tăng nóng. Thị trường có thể rung lắc khi VN Index đang tiếp cận vùng kháng cự mạnh tiếp theo 620 625.', '2015-07-07 13:42:07', '2015-07-07 14:04:48', 1, 'http://vietbao.vn/Kinh-te/Nhan-dinh-thi-truong-ngay-67-Rui-ro-gia-tang/199125166/91/'),
(9, 'Phiên chiều 03/07: Cổ phiếu ngân hàng thể hiện sức mạnh', 'Phien-chieu-03-07-Co-phieu-ngan-hang-the-hien-suc-manh', 'Nhóm cổ phiếu ngân hàng tiếp tục là tâm điểm thị trường, với mức tăng rất mạnh và giúp chỉ số VN-Index vượt mốc 615 điểm.', '<div><span class="VB_gioi_thieu">Nhóm cổ phiếu ngân hàng tiếp tục là tâm điểm thị trường, với mức tăng rất mạnh và giúp chỉ số VN-Index vượt mốc 615 điểm.</span></div> <div> <p><span class="VB_noi_dung">Kết thúc phiên giao dịch, chỉ số VN-Index dừng lại ở mức 616,43 điểm, tăng 10,73 điểm (1,77%). Tổng khối lượng giao dịch đạt 164,5 triệu đơn vị, trị giá 2.669 tỷ đồng. Toàn sàn có 121 mã tăng, 80 mã giảm và 103 mã đứng giá.</span></p> <div align="center"> <p><span class="VB_noi_dung"><img src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_56adf01496-1-252VNI-2015-07-033.png" style="max-width:450px" alt="Phien chieu 0307 Co phieu ngan hang the hien suc manh" border="0" /></span></p> </div> <p><span class="VB_noi_dung">Trên sàn Hà Nội, chỉ số HNX-Index đứng ở mức 87,70 điểm, tăng 0,94 điểm (1,08%). Tổng khối lượng giao dịch đạt 57,57 triệu đơn vị, trị giá 710,2 tỷ đồng. Toàn sàn có 101 mã tăng, 81 mã giảm và 177 mã đứng giá.</span></p> <div align="center"> <p><span class="VB_noi_dung"><img src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_dbcb9d5410-2-9d9HNX-2015-07-033.png" style="max-width:450px" alt="Phien chieu 0307 Co phieu ngan hang the hien suc manh" border="0" /></span></p> </div> <p><span class="VB_noi_dung">Tương tự như phiên giao dịch trước đó, ngân hàng tiếp tục là nhóm cổ phiếu đầu tàu giúp hai chỉ số bứt phá mạnh. Phiên giao dịch hôm nay, các cổ phiếu như VCB, BID, ACB, SHB… tiếp tục tăng rất mạnh. Trong đó, tiêu biểu nhất vẫn là VCB, mã này tiếp tục tăng tới 3.000 đồng lên 54.500 đồng/CP. Tương tự, BID tăng 1.200 đồng lên 24.600 đồng/CP và khớp lệnh được hơn 2,7 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Bên cạnh đó, nhiều cổ phiếu trụ cột khác như GAS, VIC, VNM, SSI… cùng đồng loạt tăng giá. Khép phiên giao dịch, BVH tăng mạnh 2.500 đồng lên 45.500 đồng/CP. SSI tăng 900 đồng lên 26.000 đồng/CP. VIC tăng 700 đồng lên 44.600 đồng/CP.</span></p> <p><span class="VB_noi_dung">Trong khi đó, nhóm cổ phiếu dầu khí trên sàn HNX như PGS, PVC, PVS… cũng giao dịch khởi sắc ở phiên hôm nay và cùng với các cổ phiếu có tính dẫn dắt khác là ACB, SHS, DBC… đẩy chỉ số HNX-Index lên khá mạnh.</span></p> <p><span class="VB_noi_dung">Tuy nhiên, lực tăng mạnh ở phiên hôm nay tập trung chủ yếu vào nhóm cổ phiếu bluechips, trong khi đó, các cổ phiếu vừa và nhỏ lại phân hóa tương đối mạnh. Đáng chú ý, HAI bất ngờ tăng kịch trần với dư mua giá trần lên tới hơn 11,5 triệu đơn vị. Tương tự, JVC cũng tăng trần và dư mua giá trần hơn 4,6 triệu đơn vị. Hai cổ phiếu khoáng sản là KSS và KTB vẫn được kéo lên mức giá trần.</span></p> <p><span class="VB_noi_dung">Giao dịch trên thị trường phiên hôm nay diễn ra khá sôi động, FLC vẫn dẫn đầu khối lượng khớp lệnh sàn HOSE, đạt hơn 15,6 triệu đơn vị, tương tự, SHB dẫn đầu khối lượng giao dịch sàn HNX với gần 6 triệu đơn vị.</span></p> <hr /> <p><span class="VB_noi_dung">Tạm dừng phiên giao dịch buổi sáng, chỉ số VN-Index dừng lại ở mức 615,44 điểm, tăng 9,74 điểm (1,61%). Tổng khối lượng giao dịch đạt 97,37 triệu đơn vị, trị giá 1.610,96 tỷ đồng. Toàn sàn có 101 mã tăng, 72 mã giảm và 131 mã đứng giá.</span></p> <p><span class="VB_noi_dung">Trên sàn Hà Nội, chỉ số HNX-Index đứng ở mức 87,60 điểm, tăng 0,84 điểm (0,97%). Tổng khối lượng giao dịch đạt 37,8 triệu đơn vị, trị giá 456,6 tỷ đồng. Toàn sàn có 73 mã tnawg, 62 mã giảm và 224 mã đứng giá.</span></p> <p><span class="VB_noi_dung">Phiên giao dịch sáng nay, các cổ phiếu dòng ngân hàng tiếp tục đóng vai trò dẫn dắt thị trường và giúp hai chỉ số có được mức tăng điểm mạnh, trong đó, chỉ số VN-Index tiếp tục vượt qua mốc 615 điểm.</span></p> <p><span class="VB_noi_dung">Mặc dù có phần hơi rung lắc ở giữa phiên sáng, tuy nhiên, lực cầu ở nhóm cổ phiếu ngân hàng vẫn rất vững nên các cổ phiếu như BID, CTG, VCB, ACB… vẫn duy trì được đà tăng mạnh. Trong đó, VCB tiếp tục tăng 3.000 đồng lên 54.500 đồng/CP. BID cũng tăng 1.400 đồng lên 24.800 đồng/CP và khớp lệnh hơn 2 triệu đơn vị. Đáng chú ý, có thời điểm ở phiên sáng, hai mã này đã được kéo lên mức giá trần.</span></p> <p><span class="VB_noi_dung">Bên cạnh nhóm cổ phiếu ngân hàng, phiên sáng nay, nhiều cổ phiếu thuộc dòng chứng khoán và dầu khí cũng đồng loạt tăng giá. Trong đó, những thông tin liên quan đến Nghi Định 60 tiếp tục hỗ trợ các cổ phiếu chứng khoán như SSI, HCM, VND, SHS, BVS… tăng giá. Khép phiên sáng, SSI tăng 500 đồng lên 25.600 đồng/CP và khớp lệnh được hơn 5,7 triệu đơn vị. SHS tăng 500 đồng lên 8.500 đồng/CP và khớp lệnh hơn 1,6 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Ở nhóm cổ phiếu vừa và nhỏ, HHS có biến động với biên độ rất rộng, với giao dịch tăng vọt lên mức 6,8 triệu đơn vị. Khép phiên sáng, HHS tăng trở lại 600 đồng lên 21.100 đồng/CP. Trước đó, cổ phiếu HHS đã bị hàng loạt các CTCK như MSBS, VDSC, BSC… cắt margin.</span></p> <p><span class="VB_noi_dung">Trong khi đó, JVC vẫn vững vàng ở mức giá trần, với dư mua giá trần hơn 4,2 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Phiên sáng nay, giao dịch trên thị trường diễn ra tương đối sôi động, thanh khoản hai sàn ở mức khá. Khép phiên sáng, FLC là cổ phiếu dẫn đầu về khối lượng khớp lệnh sàn HOSE, đạt hơn 10,3 triệu đơn vị. Trong khi đó, vị trí số 1 về khối lượng khớp lệnh trên sàn HNX thuộc về mã SHB, đạt hơn 4,9 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Đáng chú ý, mã EIB tiếp tục có thỏa thuận hơn 4 triệu cổ phiếu, trị giá trên 54,4 tỷ đồng.</span></p> <hr /> <p><span class="VB_noi_dung">Tiếp đà hưng phấn từ phiên giao dịch hôm qua, mở cửa phiên giao dịch cuối tuần, lực cầu mạnh tiếp tục đẩy các cổ phiếu ngân hàng trên thị trường bứt phá mạnh, đây cũng là nhân tố chính giúp hai chỉ số đang có được đà tăng mạnh.</span></p> <p><span class="VB_noi_dung">Hiện tại, BID đang tăng 900 đồng lên 24.300 đồng/CP. VCB tăng 1.500 đồng lên 53.000 đồng/CP. Phiên hôm qua, hai cổ phiếu này đã được kéo lên mức giá trần. Bên cạnh đó, CTG cũng đang tăng mạnh 500 đồng lên 20.300 đồng/CP. ACB tăng 600 đồng lên 22.300 đồng/CP.</span></p> <p><span class="VB_noi_dung">Được biết, VMAC đã mua hơn 42 ngàn tỷ đồng nợ xấu trên tổng số 54 ngàn tỷ (78%) trong nửa đầu năm 2015. Số lượng nợ xấu trên được thanh toán bằng trái phiếu đặc biệt có trị giá 40 ngàn tỷ đồng, tức là dòng tiền mới này được đưa vào lưu thông trở lại. NHNN cũng cam kết sẽ đưa nợ xấu toàn hệ thống về dưới 3%. Việc mua nợ xấu sẽ giúp ngân hàng cải thiện tài sản, giảm lãi suất cho vay và nâng biên lợi nhuận đồng thời cũng đưa tiền trở lại lưu thông bắng cách thế chấp trái phiếu đặc biệt này cho NHNN.</span></p> <p><span class="VB_noi_dung">Sắc xanh ở nhóm cổ phiếu ngân hàng đã lan tỏa tới nhiều cổ phiếu lớn khác trên thị trường. Trong đó, các mã như BVH, VIC, SSI, VCG, VND… cũng đã đồng loạt tăng giá.</span></p> <p><span class="VB_noi_dung">Ở nhóm cổ phiếu vừa và nhỏ, sau 3 phiên giảm sàn liên tiếp, lực cầu bắt đáy đã giúp HHS thoát khỏi mức giá sàn. Đáng chú ý, trong những phút đầu của phiên giao dịch, HHS có lúc vươn lên mức giá 21.800 đồng/CP, tuy nhiên, lực cung vẫn quá mạnh nên cổ phiếu này đã giảm trở lại 500 đồng xuống 20.00 đồng/CP và khớp lệnh mạnh nhất sàn HOSE, đạt hơn 5 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Trong khi đó, JVC tiếp tục được kéo lên mức giá trần và có dư mua giá trần tới gần 3 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Đến 09:30, chỉ số VN-Index đứng ở mức 612,83 điểm, tăng 7,13 điểm (1,18%). Tổng khối lượng giao dịch đạt 18,65 triệu đơn vị, trị giá 306,25 tỷ đồng. Tương tự, chỉ số HNX-Index cùng thời điểm đứng ở mức 87,78 điểm, tăng 1,02 điểm (1,18%). Tổng khối lượng giao dịch đạt 9 triệu đơn vị, trị giá 108,16 tỷ đồng.</span></p> <p><span class="VB_noi_dung"> </span></p> </div>', 4, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/56adf01496-1-252VNI-2015-07-033.png', 0, '2015-07-03 15:13:33', 'khối lượng giao dịch,phiên giao dịch,Chỉ Số Vn-Index,tổng khối lượng,đáng chú ý,cổ phiếu,đơn vị,ngân hàng,tiếp tục,giá trần,trị giá,đồng,triệu,điểm,mã', 'Nhóm cổ phiếu ngân hàng tiếp tục là tâm điểm thị trường, với mức tăng rất mạnh và giúp chỉ số VN-Index vượt mốc 615 điểm. ', '2015-07-07 13:42:07', '2015-07-07 14:04:48', 1, 'http://vietbao.vn/Kinh-te/Phien-chieu-0307-Co-phieu-ngan-hang-the-hien-suc-manh/2147574824/91/');
INSERT INTO `tbl_news_content` (`id`, `title`, `alias`, `introtext`, `fulltext`, `catid`, `uid`, `thumbnail`, `ordering`, `created`, `metakey`, `metadesc`, `cdate`, `mdate`, `status`, `link_original`) VALUES
(10, 'VN-Index bứt phá gần 9 điểm, vượt mốc 625', 'VN-Index-but-pha-gan-9-diem-vuot-moc-625', 'BVH và PVD đã được kéo lên mức giá trần.', '<p class="initContent" id="initcontent" style="padding: 0 0 10px 0px; font-size: 14px; line-height: 22px;"><span class="VB_gioi_thieu">BVH và PVD đã được kéo lên mức giá trần.</span></p> <p><span class="VB_noi_dung">Hết thời gian giao dịch, chỉ số VN-Index dừng lại ở mức 625,22 điểm, tăng 8,79 điểm (1,43%). Tổng khối lượng giao dịch đạt 179,74 triệu đơn vị, trị giá 3.108,69 tỷ đồng. Toàn sàn có 142 mã tăng, 80 mã giảm và 82 mã đứng giá.</span></p> <p><span class="VB_noi_dung"><img src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_5ecb115e67-1-a96VNI-2015-07-063.png" style="max-width:450px" alt="VNIndex but pha gan 9 diem vuot moc 625" border="0" /></span></p> <p><span class="VB_noi_dung">Trên sàn Hà Nội, chỉ số HNX-Index đứng ở mức 89,47 điểm, tăng 1,76 điểm (2,01%). Tổng khối lượng giao dịch đạt 65,3 triệu đơn vị, trị giá 888 tỷ đồng. Toàn sàn có 114 mã tăng, 86 mã giảm và 159 mã đứng giá.</span></p> <p><span class="VB_noi_dung"><img src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_5803f48ce1-2-281HNX-2015-07-063.png" style="max-width:450px" alt="VNIndex but pha gan 9 diem vuot moc 625" border="0" /></span></p> <p><span class="VB_noi_dung">Cuối phiên giao dịch, lực cầu tiếp tục gia tăng mạnh và giúp nới rộng sắc xanh của hai chỉ số. Tương tự như một vài phiên gần đây, động lực tăng chính của thị trường phiên hôm nay chủ yếu đến từ nhóm cổ phiếu bluechips.</span></p> <p><span class="VB_noi_dung">Đáng chú ý dòng tiền trong phiên hôm nay đã chuyển dịch khá mạnh vào nhóm cổ phiếu dầu khí và khiến các mã như PVS, GAS, PVD, PGS, PVC… bật tăng rất mạnh. Trong đó, PVD đã được kéo lên mức giá trần. GAS tăng 1.500 đồng lên 65.000 đồng/CP. PVS tăng 1.700 đồng lên 30.000 đồng/CP và khớp lệnh hơn 5 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Phiên hôm nay, nhóm ngân hàng không còn duy trì được động lực tăng mạnh như các phiên trước, tuy nhiên, một số mã như BID và ACB vẫn có được mức tăng khá mạnh. Khép phiên giao dịch, BID tăng 700 đồng lên 25.300 đồng/CP và khớp lệnh hơn 2,8 triệu đơn vị. ACB tăng 1.100 đồng lên 23.400 đồng/CP và khớp lệnh hơn 2,5 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Tương tự như nhóm ngân hàng, đà tăng mạnh ở nhóm chứng khoán cũng suy yếu đi đáng kể. Phiên hôm nay, SSI tiếp tục giữ vai trò dẫn dắt dòng cổ phiếu này với mức tăng 500 đồng lên 26.500 đồng/CP và khớp lệnh hơn 6,3 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Bên cạnh đó, các cổ phiếu lớn khác như BVH, VNM, MSN, GMD... cũng đã tăng mạnh. Trong đó, BVH đã được kéo lên mức giá trần.</span></p> <p><span class="VB_noi_dung">Ở nhóm cổ phiếu vừa và nhỏ, các cổ phiếu như JVC, FLC, KBC… đã hút dòng tiền khá tốt. Đáng chú ý, JVC tiếp tục có phiên tăng trần thứ 3 liên tiếp và vươn lên khớp lệnh mạnh nhất sàn HOSE, đạt hơn 9,3 triệu đơn vị. Tương tự, KSS cũng có phiên tăng trần thứ 4 liên tiếp và khớp lệnh hơn 5 triệu đơn vị. Trong khi đó, cổ phiếu dẫn đầu về giao dịch trên sàn HNX là SCR, với hơn 5,3 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Phiên hôm nay, giao dịch thỏa thuận lớn được thực hiện trên các mã là HAG (2 triệu cổ phiếu), IDI (6 triệu cổ phiếu) và RDP (5,6 triệu cổ phiếu).</span></p> <p><span class="VB_noi_dung">--------------------------</span></p> <p><span class="VB_noi_dung">Tạm dừng phiên giao dịch buổi sáng, chỉ số VN-Index dừng lại ở mức 619,58 điểm, tăng 3,15 điểm (0,51%). Tổng khối lượng giao dịch đạt 85,7 triệu đơn vị, trị giá 1.619,6 tỷ đồng. Toàn sàn có 113 mã tăng, 87 mã giảm và 104 mã đứng giá.</span></p> <p><span class="VB_noi_dung">Tương tự, chỉ số HNX-Index đứng ở mức 88,42 điểm, tăng 0,72 điểm (0,82%). Tổng khối lượng giao dịch đạt 34,8 triệu đơn vị, trị giá 476 tỷ đồng. Toàn sàn có 77 mã tăng, 79 mã giảm và 203 mã đứng giá.</span></p> <p><span class="VB_noi_dung">Sau khi giao dịch hết sức khởi sắc ở khoảng thời gian đầu của phiên sáng (chỉ số VN-Index vượt 620 điểm) lực bán có phần tăng mạnh đã khiến nhiều cổ phiếu lớn trên thị trường đảo chiểu giảm giá, bên cạnh đó, đà tăng của nhiều cổ phiếu trụ cột khác cũng phần nào bị giảm sút.</span></p> <p><span class="VB_noi_dung">Phiên sáng nay, dòng tiền từ các nhóm cổ phiếu ngân hàng và chứng khoán có phần chuyển dịch sang các cổ phiếu dòng dầu khí. Khép phiên sáng, các mã như GAS, PVD, PVC, PVS… đã đồng loạt tăng giá. Trong đó, GAS tăng 500 đồng lên 64.000 đồng/CP. PVD tăng mạnh 2.500 đồng lên 57.000 đồng/CP. PVS tăng 400 đồng lên 28.700 đồng/CP và khớp lệnh gần 2,3 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Trong khi đó, các cổ phiếu dòng ngân hàng và chứng khoán về cuối phiên sáng đã có sự phân hóa đáng kể. Các mã như VCB, STB, HCM, SHB, KLS… đã lùi xuống dưới mốc tham chiếu. Trong khi đó, sắc xanh vẫn được duy trì khá tốt trên mã SSI, BID, CTG, ACB. Khép phiên sáng, ACB tăng 700 đồng lên 23.000 đồng/CP và khớp lệnh hơn 1,6 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Một số cổ phiếu lớn khác trên thị trường như BVH, GMD, HPG, VNM… cũng duy trì được đà tăng.</span></p> <p><span class="VB_noi_dung">Ở nhóm cổ phiếu vừa và nhỏ, mã HHS đã được kéo lên mức giá trần và khớp lệnh hơn 1,4 triệu đơn vị.</span></p> <p><span class="VB_noi_dung">Giao dịch trên thị trường phiên sáng nay diễn ra tương đối sôi động. SSI tăng 400 đồng lên 26.400 đồng/CP và khớp lệnh mạnh nhất sàn HOSE, đạt hơn 4,3 triệu đơn vị. Bên cạnh đó, SCR tăng 100 đồng lên 8.700 đồng/CP và khớp lệnh mạnh nhất sàn HNX, đạt hơn 3,2 triệu đơn vị.</span></p> <hr /> <p><span class="VB_noi_dung">Sau một tuần giao dịch khá khởi sắc, VCBS cho rằng tâm lý hưng phấn của thị trường nhiều khả năng sẽ tiếp tục được kích hoạt trong tuần tiếp theo. Mặc dù sức lan tỏa của lực cầu và dòng tiền chưa cao khi nhiều nhóm cổ phiếu vẫn chỉ nhích nhẹ và gần như đi ngang nhưng một vài nhóm ngành như Chứng khoán, Bất động sản đã bắt đầu có tín hiệu tích cực hơn. Diễn biến này đem lại kỳ vọng về việc khoảng cách giá giữa các nhóm cổ phiếu có thể sớm được thu hẹp và tạo nên mặt bằng giá mới cho thị trường. Sự luân chuyển tốt dòng vốn, nếu được duy trì, là tín hiệu tích cực củng cố cho xu hướng tăng của hai chỉ số trong tuần tới. Tuy vậy, nhà đầu tư vẫn cần theo dõi thêm diễn biến cung – cầu khi tâm lý thị trường nói chung vẫn đang trong tình trạng nghi ngờ và chưa hoàn toàn tin tưởng vào đà tăng.</span></p> <p><span class="VB_noi_dung">Nhà đầu tư có thể cân nhắc lựa chọn giải ngân vào các cổ phiếu blue-chips, có yếu tố cơ bản tốt và đặc biệt là thu hút mạnh dòng vốn ngoại để lướt sóng ngắn hạn. Trong khi đó, sự thận trọng vẫn là cần thiết và hành động mua đuổi các mã đã tăng nhiều không được chúng tôi khuyến khích.</span></p> <p><span class="VB_noi_dung">Bước vào phiên giao dịch đầu tuần mới, thị trường tiếp tục giao dịch khá khởi sắc, trong đó, nổi bật nhất vẫn là nhóm cổ phiếu chứng khoán và ngân hàng. Hiện tại, các cổ phiếu chứng khoán như SSI, HCM, VND, SHS… tiếp tục bứt phá. Mã SSI đang tăng 700 đồng lên 26.700 đồng/CP và khớp lệnh được hơn 1 triệu đơn vị. Tương tự, SHS cũng đang tăng 300 đồng lên 8.700 đồng/CP.</span></p> <p><span class="VB_noi_dung">Bên cạnh đó, nhiều cổ phiếu ngân hàng như BID, CTG, MBB, STB, ACB… vẫn tiếp tục tăng giá và tạo lực đỡ khá lớn giúp hai chỉ số có được mức tăng điểm tương đối mạnh. Hiện giờ, ACB đang tăng 400 đồng lên 22.700 đồng/CP. BID tăng mạnh 300 đồng lên 25.400 đồng/CP.</span></p> <p><span class="VB_noi_dung">Ngoài ra, các cổ phiếu trụ cột khác trên thị trường là GAS, VIC, VNM, KDC, PVS, PGS… đều đã đồng loạt tăng giá.</span></p> <p><span class="VB_noi_dung">Ở nhóm cổ phiếu vừa và nhỏ, PVT đã được kéo lên mức giá trần ngay từ đầu phiên giao dịch. Tương tự, JVC đang có phiên tăng trần thứ 3 liên tiếp, với lượng dư mua giá trần gần 3 triệu đơn vị. HHS cũng đang giao dịch sát mức giá trần (22.900 đồng/CP).</span></p> <p><span class="VB_noi_dung">Đến 09:30, chỉ số VN-Index đứng ở mức 622,75 điểm, tăng 6,32 điểm (1,03%). Tổng khối lượng giao dịch đạt 16,6 triệu đơn vị, trị giá 299,7 tỷ đồng.</span></p> <p><span class="VB_noi_dung">Trên sàn Hà Nội, chỉ số HNX-Index cùng thời điểm đứng ở mức 88,62 điểm, tăng 0,92 điểm (1,05%). Tổng khối lượng giao dịch đạt 6,4 triệu đơn vị, trị giá 102,28 tỷ đồng.</span></p>', 4, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/5ecb115e67-1-a96VNI-2015-07-063.png', 0, '2015-07-06 15:26:10', 'khối lượng giao dịch,tổng khối lượng,phiên giao dịch,bên cạnh đó,Chỉ Số Vn-Index,cổ phiếu,đơn vị,Khớp lệnh,thị trường,mức giá,tăng mạnh,triệu,đồng,mã,điểm', ' BVH và PVD đã được kéo lên mức giá trần. ', '2015-07-07 13:42:07', '2015-07-07 14:04:48', 1, 'http://vietbao.vn/Kinh-te/VNIndex-but-pha-gan-9-diem-vuot-moc-625/2147575349/91/'),
(11, 'VietinBank dành 10.000 tỷ đồng cho vay ưu đãi 6,5%/năm', 'VietinBank-danh-10000-ty-dong-cho-vay-uu-dai-6-5-nam', 'VietinBank vừa triển khai gói tín dụng 10.000 tỷ ưu đãi dành cho khách hàng cá nhân vay vốn với lãi suất từ 6,5%/năm.', '<b>VietinBank vừa triển khai gói tín dụng 10.000 tỷ ưu đãi dành cho khách hàng cá nhân vay vốn với lãi suất từ 6,5%/năm.</b> <p>Từ ngày 1/7/2015 đến hết ngày 30/9/2015, VietinBank triển khai gói tín dụng 10.000 tỷ đồng ưu đãi dành cho khách hàng cá nhân vay vốn với lãi suất hấp dẫn từ 6,5%/năm…</p> <p>Tiếp nối thành công của các chương trình cho vay với lãi suất ưu đãi, VietinBank tiếp tục triển khai chương trình “10.000 tỷ ưu đãi lãi vay” trên phạm vi toàn quốc. Đối tượng của chương trình là khách hàng cá nhân, hộ gia đình, doanh nghiệp siêu vi mô có nhu cầu vay vốn phục vụ mục đích tiêu dùng và sản xuất kinh doanh.</p> <p>Với chương trình “10.000 tỷ ưu đãi lãi vay”, VietinBank áp dụng mức lãi suất linh hoạt, giúp khách hàng chủ động cân đối nguồn tài chính và an tâm hơn cho những kế hoạch tài chính khác của cá nhân và gia đình, cũng như kế hoạch kinh doanh dài hạn.</p> <table> <tbody> <tr> <td><img src="http://a9.vietbao.vn/images/vn999/181/2015/07/20150706-vietinbank-danh-10000-ty-dong-cho-vay-uu-dai-6-5-nam-1.jpg" alt="VietinBank danh 10.000 ty dong cho vay uu dai 6,5%/nam" /></td> </tr> <tr> <td><em>VietinBank dành 10.000 tỷ đồng cho vay ưu đãi 6,5%/năm</em></td> </tr> </tbody> </table> <p>Cụ thể, đối với vay vốn tiêu dùng, khách hàng sẽ được hưởng mức lãi suất chỉ từ 7%/năm. Còn đối với khoản vay sản xuất kinh doanh dành cho hộ gia đình, doanh nghiệp siêu vi mô, khách hàng được hưởng mức lãi suất ưu đãi chỉ từ 6,5%/năm. Đây không chỉ được đánh giá là mức lãi suất hấp dẫn, gói 10.000 tỷ đồng cũng được xem như là gói tín dụng lớn nhất thị trường hiện nay, đảm bảo cho khách hàng an tâm về khoản vay cũng như thời gian giải ngân.</p> <p>Đặc biệt, khách hàng được giải ngân trong thời gian từ nay đến hết 20/7/2015 sẽ có cơ hội hoàn tiền đến 100% giá trị khoản vay (tối đa 500 triệu đồng) trong chương trình quay số may mắn “Hiện thực những mong ước” (với mỗi 50 triệu đồng giải ngân khách hàng nhận 01 mã dự thưởng may mắn, giải ngân càng nhiều cơ hội trúng càng cao).</p> <p>Mọi thông tin chi tiết về chương trình, tìm hiểu tại đây hoặc liên hệ 1900 558 868 và các Chi nhánh VietinBank trên toàn quốc./.</p> <p>Hoàng Ngân</p> <div class="vivamain181" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("181"); //]]> </script></div>', 5, 0, 'http://img.vietbao.vn/images/2000/vn999/181/2015/07/20150706-vietinbank-danh-10000-ty-dong-cho-vay-uu-dai-6-5-nam-1.jpg', 0, '2015-07-06 11:35:00', 'khách hàng cá nhân,ưu đãi,lãi suất,cho vay,chương trình,vay vốn,tín dụng,Vietinbank,tỷ,đồng,gói,doanh', 'VietinBank vừa triển khai gói tín dụng 10.000 tỷ ưu đãi dành cho khách hàng cá nhân vay vốn với lãi suất từ 6,5%/năm.', '2015-07-07 13:42:08', '2015-07-07 14:04:49', 1, 'http://vietbao.vn/Kinh-te/VietinBank-danh-10000-ty-dong-cho-vay-uu-dai-6-5nam/181411794/87/'),
(12, 'Xử lý nợ xấu 6 tháng đầu năm vẫn ì ạch!', 'Xu-ly-no-xau-6-thang-dau-nam-van-i-ach', 'Trái ngược với sự quyết liệt của NHNN trong việc xử lý nợ xấu, tình hình thực tế tại từng ngân hàng cho thấy, tiến độ này đang khá ì ạch.', '<div><span class="VB_gioi_thieu">Trái ngược với sự quyết liệt của NHNN trong việc xử lý nợ xấu, tình hình thực tế tại từng ngân hàng cho thấy, tiến độ này đang khá ì ạch.</span> <p align="center"><span class="VB_noi_dung"><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150707-xu-ly-no-xau-6-thang-dau-nam-van-i-ach-1.jpg" alt="Xu ly no xau 6 thang dau nam van i ach!" height="239" width="450" /></span></p> <p align="center"><span class="VB_chu_thich_hinh">Tính đến cuối tháng 6/2015, VAMC đã mua được tổng cộng khoảng 158.000 tỷ đồng nợ xấu</span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Xử lý nợ xấu: Quyết tâm rõ ràng của NHNN</span></span></p> <p>Ngay từ đầu năm 2015, NHNN đã ban hành Chỉ thị số 02/CT-NHNN về tăng cường xử lý nợ xấu của TCTD cùng các văn bản chỉ đạo xây dựng kế hoạch xử lý nợ xấu năm 2015 với mục tiêu đưa nợ xấu về dưới 3% cho thấy, sự quyết liệt của NHNN trong vấn đề này.</p> <p>Trao đổi với ĐTCK, Tổng giám đốc một NHTM chia sẻ, ngân hàng ông thường xuyên nhận được các văn bản đôn đốc, chỉ đạo tăng cường các biện pháp xử lý nợ xấu và nâng cao chất lượng tín dụng, kèm theo áp dụng các biện pháp hỗ trợ (như quản lý cấp phép, kiểm soát tăng trưởng tín dụng…) để đẩy nhanh việc xử lý nợ xấu.</p> <p><em>“NHNN cũng đã có biện pháp hỗ trợ về việc trích lập dự phòng rủi ro đối với nợ xấu và trái phiếu đặc biệt phù hợp với khả năng tài chính của TCTD trong một số trường hợp khó khăn hoặc đang thực hiện cơ cấu lại theo phương án được phê duyệt”</em>, vị Tổng giám đốc trên nói.</p> <p>Theo một lãnh đạo cao cấp NHNN, thông qua các buổi làm việc với các TCTD, NHNN đã tổng hợp các vướng mắc, bất cập về quy định pháp luật liên quan đến xử lý nợ xấu, tài sản bảo đảm, báo cáo và kiến nghị Quốc hội, Chính phủ, Thủ tướng Chính phủ chỉ đạo xử lý. NHNN đã xây dựng và trình Thủ tướng Chính phủ Đề án mua bán nợ xấu theo cơ chế thị trường… và hiện đang khẩn trương hoàn thiện, ban hành các thông tư hướng dẫn theo hướng tạo điều kiện thuận lợi hơn cho hoạt động mua bán, xử lý nợ của VAMC và các TCTD.</p> <p>Tìm hiểu của ĐTCK, trong 4 tháng đầu năm 2015, nợ xấu đã được xử lý gần 26 nghìn tỷ đồng, lũy kế từ năm 2012 đến nay đạt khoảng 337 nghìn tỷ đồng, tương đương khoảng 73% tổng số nợ xấu ước tính tại thời điểm tháng 9/2012. Từ đầu năm 2015 đến cuối tháng 6/2015, VAMC đã duyệt mua hơn 33 nghìn tỷ đồng nợ xấu (tổng dư nợ theo hồ sơ đề nghị đã tiếp nhận gần 50.000 tỷ đồng), lũy kế kể từ khi thành lập đến cuối tháng 6/2015, VAMC đã mua được khoảng 158 nghìn tỷ đồng nợ xấu. </p> <p><span class="VB_tieu_de_phu">Tình hình xử lý nợ xấu của các ngân hàng đến đâu?</span></p> <p>Theo Chỉ thị số 02, đến ngày 30/6/2015, TCTD phải xử lý được tối thiểu 60% tổng số nợ xấu phải xử lý theo kế hoạch năm 2015, trong đó, chỉ tiêu bán nợ xấu cho VAMC phải đạt ít nhất 75% tổng số nợ xấu dự kiến bán cho VAMC cả năm 2015.</p> <p>Tuy nhiên, trao đổi với ĐTCK, Phó tổng giám đốc một ngân hàng phụ trách mảng xử lý nợ xấu cho biết, nếu tính đến thời điểm cuối năm 2014, nợ xấu của ngân hàng vào khoảng 5%, thì thời điểm hiện tại, con số nợ xấu mới giảm được khoảng 1% trên tổng dư nợ. Đến cuối tháng 6/2015, số nợ xấu bán cho VAMC mới đạt khoảng 1/4 kế hoạch 6 tháng đầu năm.</p> <p>Lý giải vì sao việc xử lý nợ xấu diễn ra chậm chạp, vị lãnh đạo này cho biết, xử lý nợ xấu không phải là câu chuyên riêng của bộ phận xử lý nợ mà đó là hoạt động cần sự phối hợp, hỗ trợ của nhiều phòng, ban khác trong toàn ngân hàng, đặc biệt là các lãnh đạo cấp cao. Cụ thể, tăng trưởng tín dụng; phát triển sản phẩm dịch vụ; trích lập dự phòng và siết chặt hơn nữa quản trị rủi ro, đề phòng nợ xấu mới phát sinh…</p> <p><em>“Liên quan đến bán nợ xấu cho VAMC chậm chạp một phần do hồ sơ bán nợ phải được thẩm định tại ngân hàng rất kỹ mới chuyển lên VAMC. Bên cạnh đó, nguyên nhân chủ quan là nguồn nhân lực của VAMC cũng hạn chế trong khi khối lượng công việc lớn nên quá trình xét duyệt hồ sơ tại cơ quan này cũng có những chậm trễ nhất định”</em>, vị Phó tổng giám đốc trên nói.</p> <p>Bên cạnh đó, thực tế việc ngân hàng phải trích dự phòng rủi ro 20%/năm giá trị nợ xấu đã bán cho VAMC cũng khiến lợi nhuận ngân hàng bị giảm mạnh, nên không ít ngân hàng không mấy mặn mà bán nợ xấu.</p> <p>Ngoài ra, theo Tổng giám đốc một ngân hàng, xử lý nợ xấu chậm còn có một nguyên nhân nữa là, việc thu hồi tài sản của ngân hàng, đặc biệt là tài sản liên quan đến các vụ án, cũng như việc tháo gỡ khó khăn, vướng mắc pháp lý liên quan đến xử lý nợ, tài sản bảo đảm vẫn còn nhiều rào cản.</p> <p>Trên thực tế, các TCTD vẫn tiếp tục cơ cấu lại nợ nhằm hỗ trợ khách hàng giảm bớt áp lực tài chính và phục hồi sản xuất - kinh doanh, tuy nhiên, điều kiện cơ cấu lại nợ đã được thắt chặt hơn theo quy định của Thông tư 09/2014/TT-NHNN. Tính đến cuối tháng 4/2015, tổng số dư các khoản nợ được cơ cấu thời hạn trả nợ và giữ nguyên nhóm nợ là xấp xỉ 310 nghìn tỷ đồng, giảm hơn 1 nghìn tỷ đồng so với cuối tháng 12/2014.</p> <p><em>“Trong 6 tháng đầu năm, nợ xấu của ngân hàng đã tăng hơn 9 nghìn tỷ đồng và một trong những nguyên nhân chính là Quyết định 780 hết hiệu lực, món nợ ‘nhảy’ nhóm, khiến nợ xấu bị dồn ứ”</em>, lãnh đạo một ngân hàng cho biết.</p> <p>Có lẽ, với những khó khăn của nền kinh tế hiện nay, những bất cập về pháp lý liên quan đến xử lý nợ xấu, tài sản bảo đảm chậm được tháo gỡ, sản xuất - kinh doanh và thị trường bất động sản phục hồi chậm, năng lực của VAMC còn hạn chế…, nợ xấu vẫn là mối đe dọa, tiềm ẩn nhiều rủi ro đối với an toàn, hiệu quả hoạt động của các TCTD. </p> <p><br /></p> <div class="vivamain199" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></div> </div>', 5, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150707-xu-ly-no-xau-6-thang-dau-nam-van-i-ach-1.jpg', 0, '2015-07-07 07:57:31', 'tình hình thực tế,xử lý nợ,liên quan đến,tổng giám đốc,sự quyết liệt,đầu năm,6 tháng,nghìn tỷ,ì ạch,cuối tháng,bán nợ,xấu,việc,đồng,chính', 'Trái ngược với sự quyết liệt của NHNN trong việc xử lý nợ xấu, tình hình thực tế tại từng ngân hàng cho thấy, tiến độ này đang khá ì ạch.', '2015-07-07 13:42:08', '2015-07-07 14:04:49', 1, 'http://vietbao.vn/Kinh-te/Xu-ly-no-xau-6-thang-dau-nam-van-i-ach/199125216/90/'),
(13, 'Huy động trái phiếu chính phủ sẽ có thách thức', 'Huy-dong-trai-phieu-chinh-phu-se-co-thach-thuc', 'Chủ trương giảm động viên từ thuế, phí vào NSNN nhằm hỗ trợ thị trường, thúc đẩy sản xuất - kinh doanh đã làm hạn chế nguồn thu ngân sách, trong khi đó, nền kinh tế còn nhiều khó khăn, nhu cầu chi cho an sinh - xã hội ngày càng tăng, đầu tư của khu vực tư', '<b>Chủ trương giảm động viên từ thuế, phí vào NSNN nhằm hỗ trợ thị trường, thúc đẩy sản xuất - kinh doanh đã làm hạn chế nguồn thu ngân sách, trong khi đó, nền kinh tế còn nhiều khó khăn, nhu cầu chi cho an sinh - xã hội ngày càng tăng, đầu tư của khu vực tư nhân còn yếu, đòi hỏi vẫn cần nguồn lực từ khu vực Nhà nước để hỗ trợ tăng trưởng.</b><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150707-huy-dong-trai-phieu-chinh-phu-se-co-thach-thuc-1.jpg" alt="Huy dong trai phieu chinh phu se co thach thuc" /> <p>Trong bối cảnh đó, nhu cầu phát hành trái phiếu chính phủ để bù đắp bội chi và đầu tư cho các công trình giao thông, thủy lợi, y tế là rất lớn.</p> <p>Theo Nghị quyết số 12/2011 của Quốc hội, tổng mức đầu tư bằng nguồn vốn trái phiếu chính phủ giai đoạn 2011 - 2015 là 255.000 tỷ đồng. Tuy nhiên, do thực hiện các chính sách miễn giảm thuế, nguồn thu bị thu hẹp trong khi nhu cầu huy động vốn cho đầu tư phát triển lớn, nên Quốc hội đã quyết định phát hành bổ sung vốn trái phiếu chính phủ giai đoạn 2014 - 2016 là 170.000 tỷ đồng. Ước tính, giai đoạn 2011 - 2015, khối lượng trái phiếu chính phủ phát hành cho đầu tư là 335.000 tỷ đồng, gấp 2,5 lần so với giai đoạn 2006 - 2010. Trong đó, năm 2015, dự kiến phát hành 85.000 tỷ đồng.</p> <p>Điều đáng chú ý là, mặc dù khối lượng trái phiếu chính phủ phát hành tăng nhanh qua các năm, nhưng lãi suất lại có xu hướng giảm. Mức lãi suất trái phiếu huy động bình quân 2012 là 9,8%/năm, giảm xuống còn 7,79%/năm (năm 2013) và 6,6%/năm (năm 2014).</p> <p>Điều này một mặt do xu hướng điều chỉnh lãi suất trên thị trường tín dụng của Ngân hàng Nhà nước, mặt khác phản ánh khả năng hấp thụ nguồn vốn tín dụng của nền kinh tế thấp, nên các tổ chức tín dụng đã tích cực tham gia vào thị trường trái phiếu, kéo lãi suất huy động trái phiếu chính phủ giảm xuống. Tăng trưởng tín dụng giai đoạn 2011 - 2014 chỉ đạt bình quân khoảng 11,8%/năm, trong khi giai đoạn 2006 - 2010 đạt bình quân khoảng 35,8%/năm,</p> <p>Tuy nhiên, với khả năng tăng trưởng kinh tế khả quan hơn trong năm 2015, đồng thời với nhu cầu phát hành trái phiếu chính phủ tăng cao và mục tiêu của Quốc hội là huy động trái phiếu với kỳ hạn dài hơn, dự báo, việc huy động trái phiếu chính phủ sẽ có những thách thức nhất định trong thời gian tới, đặc biệt là chi phí huy động vốn.</p> <p><b>TS. Vũ Như Thăng, Viện trưởng Viện Chiến lược và chính sách tài chính</b></p> <div class="vivamain199" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></div>', 5, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150707-huy-dong-trai-phieu-chinh-phu-se-co-thach-thuc-1.jpg', 0, '2015-07-07 09:00:00', 'trái phiếu chính phủ,khu vực tư nhân,nền kinh tế,Thu Ngân Sách,ngày càng tăng,của khu vực,huy động,đầu tư,phát hành,thách thức,giai đoạn,nhu cầu,giảm,hội,đồng', 'Chủ trương giảm động viên từ thuế, phí vào NSNN nhằm hỗ trợ thị trường, thúc đẩy sản xuất   kinh doanh đã làm hạn chế nguồn thu ngân sách, trong khi đó, nền kinh tế còn nhiều khó khăn, nhu cầu chi cho an sinh   xã hội ngày càng tăng, đầu tư của khu vực tư', '2015-07-07 13:42:08', '2015-07-07 14:04:49', 1, 'http://vietbao.vn/Kinh-te/Huy-dong-trai-phieu-chinh-phu-se-co-thach-thuc/199125262/91/'),
(14, 'Cơ hội vốn nào cho doanh nghiệp nhập khẩu khi lãi suất tăng?', 'Co-hoi-von-nao-cho-doanh-nghiep-nhap-khau-khi-lai-suat-tang', 'Các ngân hàng rục rịch tăng lãi suất tiền VND trong mấy ngày hôm qua đang tạo sự lo lắng không nhỏ cho các doanh nghiệp (DN), nhất là nhóm DN nhập khẩu về chi phí vốn. Áp lực nêu trên dường như còn nặng nề hơn khi mức tăng trưởng tín dụng năm 2015 sẽ vẫn ', '<div><span class="VB_gioi_thieu">Các ngân hàng rục rịch tăng lãi suất tiền VND trong mấy ngày hôm qua đang tạo sự lo lắng không nhỏ cho các doanh nghiệp (DN), nhất là nhóm DN nhập khẩu về chi phí vốn. Áp lực nêu trên dường như còn nặng nề hơn khi mức tăng trưởng tín dụng năm 2015 sẽ vẫn được kiểm soát ở mức 13 đến 15%, cao nhất có thể là 17% như thông tin từ cuộc họp báo ngày 25-6 của Ngân hàng Nhà nước.</span></div> <div> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Lãi suất huy động tăng</span></span></p> <p>Lãi suất huy động tiền đồng tăng đang tạo áp lực để lãi suất vay tiền đồng. Với ngân hàng, mức tỷ lệ thu nhập lãi cận biên (NIM) trung bình đang nhỏ hơn mức thông lệ ở ngưỡng sinh lời là 3%, các ngân hàng buộc phải cân đối lãi suất vay để cân đối NIM vay của mình để bù đắp chi phí. Với khách hàng vay, việc điều chỉnh lãi suất vay chắc chắn ảnh hưởng đến bài toán chi phí của họ. Hiện, mức lãi suất cho vay các lĩnh vực sản xuất, kinh doanh thông thường ở mức 7 đến 9%/năm đối với ngắn hạn và 9,5 đến 11%/năm đối với trung và dài hạn. Với tình hình này, khách hàng sẽ phải chịu mức tăng dao động từ 0.4 đến 0.5%.</p> <p>Đến thời điểm này, đã có một số ngân hàng thương mại như đang ngồi trên chảo lửa khi mức tăng trưởng tín dụng đạt xấp xỉ ngưỡng 13%. Số ngân hàng tăng kịch trần hiện chiếm 30% ngân hàng thương mại cổ phần. Sốt ruột là đúng khi phần nhiều ngân hàng thương mại cổ phần đặt cho mình kế hoạch tăng trưởng tín dụng rất thách thức, lên tới 20 đến 30%. Với trần 13% này, rõ ràng, kế hoạch lợi nhuận của họ sẽ bị ảnh hưởng trực tiếp. Nhóm ngân hàng quốc doanh xem ra vẫn còn ung dung bởi mức tăng chậm hơn và quy mô lớn hơn.</p> <p>Không thể đi ngược quy định, những chính sách điều chỉnh tín dụng đã được thực thi như: chỉ cho vay những khách hàng tốt, chỉ cho vay những khách hàng mang lại mức NIM tối tiểu, tập trung giải ngân các khách hàng đã được phê duyệt tín dụng, xoay qua khai thác bán chéo các sản phẩm thu phí khác... Thậm chí, có ngân hàng đã phải chậm lại việc tìm khách hàng mới bởi lo ngại không còn room để tăng. Các ngân hàng đang chờ đợi ngày mà NHNN điều chỉnh hạn mức tăng trưởng tín dụng lên mức 15%.</p> <p>Đối với khách hàng, nhất là cụm khách hàng DN vừa và nhỏ, nhóm DN sản xuất, câu chuyện tiếp cận vốn trong hai đến ba tháng nữa sẽ là một thách thức khi mà chắc chắn sẽ xuất hiện thêm các ngân hàng lọt vào nhóm tăng tín dụng kịch trần. Việc cho vay từ nhóm này sẽ là hạn chế. Ưu thế vốn vẫn sẽ là nhóm ngân hàng quốc doanh. Khách hàng sẽ không còn nhiều cơ hội lựa chọn nhà băng có lãi suất thấp và những ưu đãi hấp dẫn như trước đó nữa.</p> <p align="center"><img src="http://a9.vietbao.vn/images/vn999/290/2015/07/20150704-co-hoi-von-nao-cho-doanh-nghiep-nhap-khau-khi-lai-suat-tang-1.jpg" alt="Co hoi von nao cho doanh nghiep nhap khau khi lai suat tang?" height="309" width="452" /></p> <p align="center"><span class="VB_chu_thich_hinh">Cán bộ ngân hàng tiếp cận doanh nghiệp nhập khẩu.</span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Giải pháp từ LC ngoại bảng?</span></span></p> <p>Trao đổi với đại diện một ngân hàng đang phát triển tín dụng khá nhanh, trong bối cảnh lãi suất đồng VND có thể tăng, thay vì vay tiền VND, các DN nhập khẩu vẫn có thể khai thác được dòng tiền ngoại bảng được tài trợ từ các ngân hàng nước ngoài cho ngân hàng trong nước.</p> <p>Hiện, có khá nhiều ngân hàng đang cung cấp dịch vụ này với tên gọi UPAS L/C - L/C trả chậm cho phép thanh toán ngay. Theo thông tin từ đại diện VPBank - một trong các ngân hàng đang cung cấp dịch vụ này, với mức phí tối đa 4.5%, DN chỉ mất 50% chi phí vốn so với việc vay bằng tiền đồng và mở L/C thông thường. Thời hạn được tài trợ của UPAS L/C lên tới 359 ngày và như vậy, sự cam kết của các ngân hàng tài trợ nước ngoài uy tín sẽ bảo đảm cho DN nhập khẩu thanh toán đúng hạn cho đối tác.</p> <p>Ngoài chi phí vốn có lợi vượt trội, DN nhập khẩu cũng sẽ không bị phát sinh dư nợ tín dụng, không ảnh hưởng đến danh mục tín dụng của mình. Cơ hội kinh doanh là không thể chờ đợi. Rõ ràng, UPAS L/C là một giải pháp hữu hiệu trong giai đoạn này đối với doanh nghiệp nhập khẩu.</p> <div class="vivamain290" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("290"); //]]> </script></div> </div>', 5, 0, 'http://img.vietbao.vn/images/2000/vn999/290/2015/07/20150704-co-hoi-von-nao-cho-doanh-nghiep-nhap-khau-khi-lai-suat-tang-1.jpg', 0, '2015-07-04 07:39:56', 'UPAS L,ngân hàng thương mại,mức tăng trưởng,chi phí vốn,sự lo lắng,ngày hôm qua,lãi suất,nhập khẩu,tín dụng,doanh nghiệp,khách hàng,cơ hội,DN,đến,tiền', 'Các ngân hàng rục rịch tăng lãi suất tiền VND trong mấy ngày hôm qua đang tạo sự lo lắng không nhỏ cho các doanh nghiệp (DN), nhất là nhóm DN nhập khẩu về chi phí vốn. Áp lực nêu trên dường như còn nặng nề hơn khi mức tăng trưởng tín dụng năm 2015 sẽ vẫn ', '2015-07-07 13:42:08', '2015-07-07 14:04:49', 1, 'http://vietbao.vn/Kinh-te/Co-hoi-von-nao-cho-doanh-nghiep-nhap-khau-khi-lai-suat-tang/316796802/90/'),
(15, '“Lãi suất cho vay đã tăng 0,02%”', 'Lai-suat-cho-vay-da-tang-0-02', 'So với tháng 5, lãi suất huy động bình quân đã tăng 0,07% lên 5,76%. Lãi suất cho vay bình quân đã tăng 0,02% lên 9,69% vào thời điểm cuối tháng 6.', '<p class="short-text"><span class="VB_gioi_thieu">So với tháng 5, lãi suất huy động bình quân đã tăng 0,07% lên 5,76%. Lãi suất cho vay bình quân đã tăng 0,02% lên 9,69% vào thời điểm cuối tháng 6.</span></p> <div id="ctl00_main_divNormal"><img src="http://a8.vietbao.vn/images/vn888/hot/v2014/best_3dd6db637b-1-20150320-lai-suat-tiet-kiem-con-giam-den-dau-1-cibb.jpeg?width=670&height=350&crop=auto&scale=both" alt="“Lãi suất cho vay đã tăng 0,02%”" style="max-height: 350px;" border="0" width="498" height="260" /> <p id="ctl00_main_divAvatarDescription" class="note" align="center"><span class="VB_chu_thich_hinh">Ảnh minh họa.</span></p> </div> <p><span class="VB_noi_dung">Ngay những ngày đầu tháng 6, nhiều ngân hàng đã tăng lãi suất huy động. Đặc biệt, Agribank và BIDV đã tăng 0,1%-0,5% lãi suất huy động đối với một số khoản vay ngắn và dài hạn. Trong khi đó, nhiều ngân hàng nhỏ hơn Eximbank, MBB, OCB, Sacombank, DongABank, HDBank và Maritime Bank đã tăng lãi suất ngắn hạn (1-12 tháng) thêm 0,05-0,5%.</span></p> <div><span class="VB_noi_dung">Theo thống kê của CTCK HSC, lãi suất huy động bình quân đã tăng lên 5,76% vào thời điểm cuối tháng 6 hay cao hơn 0,07% so với mức thấp là 5,69% trong tháng 5. Lãi suất huy động bình quân từ đầu năm đến hiện tại đã giảm 0,28%.</span></div> <p><span class="VB_noi_dung">Trong khi đó lãi suất huy động bằng USD bình quân vẫn ổn định ở mức 0,75%. Maritime Bank đã hạ lãi suất huy động bằng USD, giảm 0,01-0,02% xuống 0,73%-0,74%. Ngoài ra, không có sự thay đổi tương tự từ các ngân hàng khác. Tháng trước, ngân hàng Bảo Việt cũng đã cắt giảm lãi suất huy động bằng USD 0,01%.</span></p> <div> <p><span class="VB_noi_dung">Đáng chú ý, lãi suất cho vay bình quân đã tăng 0,02% lên 9,69% so với mức 9,67% trong tháng 5. Mức lãi suất bình quân này vẫn giảm 0,35% từ đầu năm đến hiện tại từ mức lãi suất bình quân 10,04% theo báo cáo vào thời điểm cuối tháng 12/2014.</span></p> <p><span class="VB_noi_dung">Trong đó Vietcombank đã tăng lãi suất cho vay ngắn hạn, trong khi đó Eximbank hạ lãi suất cho vay ngắn hạn nhưng nâng lãi suất cho vay dài hạn.</span></p> </div> <div> <p><span class="VB_noi_dung">Như vậy, lo ngại về lãi suất cho vay có thể tăng theo lãi suất huy động là có cơ sở. Trước đó, CTCK MBS cũng đã dự báo lãi suất có thể tiếp tục tăng nhẹ trong thời gian tới. Việc tăng lãi suất huy động tất yếu sẽ đẩy lãi suất cho vay tăng.</span></p> <p><span class="VB_noi_dung">TS. Nguyễn Trí Hiếu, chuyên gia tài chính ngân hàng nhận định rằng, việc tăng lãi suất huy động sẽ là con dao hai lưỡi: có lợi cho một bộ phận dân chúng gửi tiền và có bất lợi cho người đi vay. Nếu ở góc độ gửi tiền ròng thì có lợi, nhưng nếu đi vay ròng thì sẽ gặp bất lợi.</span></p> </div> <div><span class="VB_noi_dung">"<em>Nhìn chung, việc tăng lãi suất là không có lợi, việc sản xuất kinh doanh sẽ khó khăn hơn, chi phí vốn cho các doanh nghiệp sẽ tăng thêm. Đồng thời tăng lãi suất là một động thái thắt chặt chính sách tiền tệ, lượng tiền vốn đổ vào nền kinh tế sẽ giảm đi dẫn đến không có lợi cho nền kinh tế, đặc biệt lãi suất của Việt Nam hiện vẫn ở mức cao</em>", ông Hiếu nhận định.<br /></span></div>', 5, 0, 'http://img.vietbao.vn/images/2000/vn888/hot/v2014/3dd6db637b-1-20150320-lai-suat-tiet-kiem-con-giam-den-dau-1-cibb.jpeg', 0, '2015-07-06 13:30:50', 'lãi suất cho vay,huy động,bình quân,thời điểm,điểm cuối,tháng 5,ngân hàng,tăng,lên,hạn,giảm', 'So với tháng 5, lãi suất huy động bình quân đã tăng 0,07% lên 5,76%. Lãi suất cho vay bình quân đã tăng 0,02% lên 9,69% vào thời điểm cuối tháng 6. ', '2015-07-07 13:42:08', '2015-07-07 14:04:49', 1, 'http://vietbao.vn/Kinh-te/Lai-suat-cho-vay-da-tang-0-02/2147575268/90/'),
(16, 'Góc nhìn khác về thanh khoản thị trường bất động sản', 'Goc-nhin-khac-ve-thanh-khoan-thi-truong-bat-dong-san', 'Những thống kê của tổ chức này, tổ chức kia về lượng giao dịch thành công tăng mạnh, thông tin dự án này, phân khúc kia đang hút hàng dễ khiến người ta có cảm giác thị trường bất động sản đang nóng lên. Nhưng thực tế con số có phải lúc nào cũng chuẩn xác?', '<div><span class="VB_gioi_thieu">Những thống kê của tổ chức này, tổ chức kia về lượng giao dịch thành công tăng mạnh, thông tin dự án này, phân khúc kia đang hút hàng dễ khiến người ta có cảm giác thị trường bất động sản đang nóng lên. Nhưng thực tế con số có phải lúc nào cũng chuẩn xác?</span> <p align="center"><span class="VB_noi_dung"><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150703-goc-nhin-khac-ve-thanh-khoan-thi-truong-bat-dong-san-1.jpg" alt="Goc nhin khac ve thanh khoan thi truong bat dong san" height="278" width="450" /></span></p> <p align="center"><span class="VB_chu_thich_hinh">Phân khúc đất nền, biệt thự vùng ven Hà Nội nhanh chóng trở lại trạng thái “ngủ đông” - Ảnh: Hoài Nam</span></p> <p><span class="VB_noi_dung">Hàng loạt thông tin phản ánh sự sôi động của dự án này, phân khúc bất động sản kia được công bố, nhiều bài trích dẫn số liệu thống kê để chứng minh thị trường bất động sản đang hồi sinh mạnh mẽ. Nhưng việc xây dựng chỉ số thị trường bất động sản lại là cả một câu chuyện.</span></p> <p><span class="VB_noi_dung">Tại thị trường lớn nhất cả nước là TP. HCM, hồi cuối tháng 12/2012, UBND TP. HCM đã ban hành quy định về đánh giá chỉ số thị trường bất động sản, giao cho Sở Xây dựng chủ trì cơ sở dữ liệu và tính toán.</span></p> <p><span class="VB_noi_dung">Theo đó, chỉ số thị trường sẽ được công bố hàng quý, hàng năm, bắt đầu từ quý I/2013.</span></p> <p><span class="VB_noi_dung">Chỉ số đánh giá thị trường bất động sản được quy định khá bài bản, trong đó, Sở Tư pháp cung cấp các hợp đồng bất động sản được công chứng; Sở Tài nguyên - Môi trường cung cấp lượng sổ đỏ, sổ hồng; Cục Thuế cung cấp số liệu về thu thuế bất động sản; các sàn bất động sản cung cấp số lượng giao dịch qua sàn. Thế nhưng, từ đó đến nay, việc xây dựng chỉ số này vẫn chưa được thực hiện.</span></p> <p><span class="VB_noi_dung">Hiện Savills vẫn là đơn vị được Sở Xây dựng TP. HCM chỉ định xây dựng chỉ số cho thị trường bất động sản. Tuy nhiên, rất khó để một đơn vị dù có tiềm lực mạnh đến đâu, đưa ra những con số tương đối chính xác về thị trường.</span></p> <p><span class="VB_noi_dung">Tại thị trường Hà Nội cũng tương tự. Những số liệu thống kê về thị trường chủ yếu từ báo cáo của Savills Việt Nam, CBRE Việt Nam được công bố đều đặn mỗi quý, trong 2 năm trở lại đây. Tuy nhiên, các chỉ số thị trường do các tổ chức này công bố chủ yếu được tổng hợp từ giao dịch qua sàn. Trong khi, nguồn thông tin từ các sàn lại hoàn toàn chưa được kiểm soát độ xác thực. </span></p> <p><span class="VB_noi_dung">Ghi nhận của Đầu tư Bất động sản cho thấy, thị trường thực tế mới chỉ thoát thế “ngủ đông”.</span></p> <p><span class="VB_noi_dung">Tại TP. HCM, giao dịch thành công chủ yếu là từ những người có nhu cầu thực sự về nhà ở. Phân khúc đất nền hầu hết là đất của các dự án vùng ven, giá mềm từ 180 - 360 triệu đồng/nền, tập trung ở Dự án Làng Sen Việt (Long An) của Phúc Khang. Các dự án tại TP. HCM có mức giá thấp hơn so với mặt bằng đất trong khu dân cư được giao dịch như Riverside City (Thủ Đức), Jamona City (quận 7), Phuoc Long Spring Town (quận 9).</span></p> <p><span class="VB_noi_dung">Phân khúc “nhà nát - đất đẹp” nội đô đang là sự lựa chọn đầu tư của người dư dả tiền bạc. Chị Kim Anh vừa mua căn nhà cũ trên diện tích 130 m2 đường Lê Văn Sỹ (quận 3) với giá 9,3 tỷ đồng cho biết: “Mức giá này không dễ để bán, còn mình thì không dễ chọn được một lô đất rộng nằm trên trục đường không bao giờ mất giá, nên tôi quyết định mua để đầu tư”.</span></p> <p><span class="VB_noi_dung">Chị Kim Anh cho biết thêm, thời điểm trước, chị chọn mua nhà ở mức 2,5 - 4,2 tỷ đồng, thời điểm đó ít người mua nên không có cạnh tranh, còn thời điểm này thì mức giá 7 - 11 tỷ đồng là hợp lý. Nguyên tắc kinh doanh bất động sản của chị Kim Anh là <em>“nhà trong phố, dễ mua, dễ bán. Cùng lắm bán lỗ vẫn bán được…”</em></span></p> <p><span class="VB_noi_dung">Tại thị trường Hà Nội, đất nền vùng ven từng “sôi” sùng sục từ truyền thông và qua cơn nóng thất thường lại trở về “ngủ đông” như cũ. Các dự án có tính thanh khoản cao thuộc về các chung cư có vị trí đẹp, thủ tục pháp lý đầy đủ, giá bán từ 21 - 27 triệu đồng/m2.</span></p> <p><span class="VB_noi_dung">Ông Nguyễn Văn Cửu, nguyên Tổng giám đốc Tổng công ty Đầu tư và Phát triển nhà Hà Nội nhận xét: <em>“Thị trường bất động sản hiện đang trong giai đoạn phục vụ cho người có nhu cầu thực, nhà đầu tư thứ cấp chưa sẵn sàng trở lại thị trường. Hiện tượng sốt chỉ xuất hiện ở một vài dự án có giá bán thấp hơn hẳn so với mặt bằng chung hoặc có tiện ích, vị trí đặc biệt”.</em></span></p> <p><span class="VB_noi_dung">Nhiều chủ đầu tư cũng tỏ ra không mấy tin tưởng vào những con số. Bà Lưu Thị Thanh Mẫu, Tổng giám đốc Công ty Phúc Khang từng thẳng thắn: <em>“Tôi không tin vào mấy số liệu thống kê, cái mà DN cần là ở vị trí này có bao nhiêu chung cư hiện tại, bao nhiêu sẽ được xây dựng trong thời gian vài năm tới, mật độ dân số, đối tượng mua… Vì thế, Phúc Khang phát triển các dự án đất nền, căn cứ trên nhu cầu thực của người mua, tính toán giá cả phù hợp, dự án phải đạt được không gian sống thế nào thì mới bán được hàng”. </em> </span></p> </div> <br /> <br /> <div class="vivamain199" align="right"><span class="VB_noi_dung">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></span></div>', 6, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150703-goc-nhin-khac-ve-thanh-khoan-thi-truong-bat-dong-san-1.jpg', 0, '2015-07-03 08:24:22', 'Hà Nội,Phúc Khang,thị trường bất động sản,chỉ số thị trường,số liệu thống kê,cảm giác thị trường,lượng giao dịch,lúc nào cũng,có cảm giác,dự án,phân khúc,tổ chức,xây dựng,đầu tư,thanh khoản,người', 'Những thống kê của tổ chức này, tổ chức kia về lượng giao dịch thành công tăng mạnh, thông tin dự án này, phân khúc kia đang hút hàng dễ khiến người ta có cảm giác thị trường bất động sản đang nóng lên. Nhưng thực tế con số có phải lúc nào cũng chuẩn xác?', '2015-07-07 13:44:38', '2015-07-07 14:04:50', 1, 'http://vietbao.vn/Nha-dat/Goc-nhin-khac-ve-thanh-khoan-thi-truong-bat-dong-san/199124587/507/');
INSERT INTO `tbl_news_content` (`id`, `title`, `alias`, `introtext`, `fulltext`, `catid`, `uid`, `thumbnail`, `ordering`, `created`, `metakey`, `metadesc`, `cdate`, `mdate`, `status`, `link_original`) VALUES
(17, 'Doanh nghiệp địa ốc nói gì về liên minh thổi giá nhà đất?', 'Doanh-nghiep-dia-oc-noi-gi-ve-lien-minh-thoi-gia-nha-dat', 'Đất tại một số khu vực, nhà tại một số dự án tại Hà Nội bị “thổi giá”. Đồn đoán này có cơ sở bởi nhiều dữ kiện khó lý giải, nhưng trong mắt nhiều doanh nghiệp địa ốc, nói “thổi giá” là chưa hẳn!', '<div><span class="VB_gioi_thieu">Đất tại một số khu vực, nhà tại một số dự án tại Hà Nội bị “thổi giá”. Đồn đoán này có cơ sở bởi nhiều dữ kiện khó lý giải, nhưng trong mắt nhiều doanh nghiệp địa ốc, nói “thổi giá” là chưa hẳn!</span> <p align="center"><span class="VB_noi_dung"><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150704-doanh-nghiep-dia-oc-noi-gi-ve-lien-minh-thoi-gia-nha-dat-1.jpg" alt="Doanh nghiep dia oc noi gi ve lien minh thoi gia nha dat?" height="265" width="450" /></span></p> <p align="center"><span class="VB_chu_thich_hinh">Từng là dự án tai tiếng, nhưng từ năm 2014 - 2015, nhiều căn hộ Dự án The Pride (quận Hà Đông) đã tăng giá 6 - 7 triệu đồng/m2</span></p> <p><span class="VB_noi_dung">Liên tiếp tăng giá bán từ năm 2014, sang năm 2015, thị trường căn hộ Hà Nội tiếp tục ghi nhận sự tăng giá ngoạn mục trong 2 quý đầu năm 2015.</span></p> <p><span class="VB_noi_dung">Cụ thể, theo thống kê của Savills Việt Nam, trong quý I/2015, giá căn hộ ở thị trường sơ cấp tăng ở hầu hết các dự án. Trong khi ở thị trường thứ cấp, mức tăng trung bình là 2%, đặc biệt nhiều khu vực, mức tăng vượt lên 11%. Đến quý II/2015, tình trạng tăng giá tiếp tục tái diễn.</span></p> <p><span class="VB_noi_dung">Báo cáo nghiên cứu mới nhất của CBRE cho biết, thị trường căn hộ Hà Nội ở thị trường thứ cấp trong quý II tăng khoảng 0,3%. Mức tăng này cao hơn ở phân khúc cao cấp và trung cấp, lần lượt là 5,6% và 5,3% so với cùng kỳ năm 2014.</span></p> <p><span class="VB_noi_dung">Tuy nhiên, theo khảo sát của phóng viên Đầu tư Chứng khoán, tại nhiều dự án, mức tăng giá còn lớn hơn nhiều các con số trên. Đặc biệt có dự án mức tăng giá lên đến 7 - 10 triệu đồng/m2 trong một thời gian ngắn, không ít dự án người mua sau phải chịu tiền chênh từ 2 - 3 triệu đồng/m2 so với giá bán trước đó. </span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Bất thường hay bình thường?</span></span></p> <p>Sự tăng giá “bất thường” ở nhiều dự án căn hộ khiến nhiều người, trong đó có cả phía đại diện Hiệp hội Bất động sản Việt Nam mới đây cũng phải đặt giả thiết về một “liên minh” thổi giá nhà đất, trong đó, vai trò chủ đạo là chủ đầu tư, đơn vị phân phối và sự tham gia của các nhà đầu tư.</p> <p>Thế nhưng, khi trao đổi về việc có hay không “liên minh” thổi giá nhà đất, hầu hết các doanh nghiệp đều phủ nhận và cho rằng, việc tăng giá bất động sản thời gian qua là đúng quy luật cung cầu của thị trường.</p> <p>Ông Trần Như Trung, Phó tổng giám đốc Tân Hoàng Minh Group thừa nhận, khi thanh khoản thị trường tăng, đối tượng muốn tăng giá bán căn hộ nhất là chủ đầu tư. Song, việc tăng giá bán trên thị trường giai đoạn vừa qua, ông Trung khẳng định là hiện tượng tự nhiên. Bởi thị trường giảm sâu trong một thời gian dài, nhiều doanh nghiệp từng phải bán nhà dưới giá thành. Việc giới đầu tư, đầu cơ sớm nắm bắt cơ hội mua vào rồi tăng giá bán kiếm lời, khiến các doanh nghiệp cũng phải điều chỉnh giá bán để tối ưu hóa lợi nhuận.</p> <p>Cũng theo ông Trung, mức tăng giá tại nhiều dự án căn hộ thời gian qua rất lớn, thậm chí xu hướng tăng giá vẫn diễn ra có vai trò rất lớn của nhà đầu cơ, đầu tư. Song, việc tăng giá theo ông Trung, vẫn khiến cả khách hàng cuối cùng mua để ở vẫn vui, không có ai kêu ca cho thấy đây là xu hướng chính của thị trường.</p> <p>Lý giải việc thị trường căn hộ Hà Nội tăng mạnh giá bán, ông Nguyễn Quốc Hiệp, Chủ tịch GPInvest lại cho rằng: Việc tăng giá bán căn hộ tại nhiều dự án thời gian gần đây xuất phát từ việc tăng giá đất. Cụ thể, giá đất năm 2014 - 2015 đã tăng từ 150 - 200% so với giai đoạn 2011 - 2012, nên việc chủ đầu tư điều chỉnh tăng giá bán là điều dễ hiểu. Ngoài ra, yếu tố thị trường tốt lên cũng khiến doanh nghiệp điều chỉnh tăng giá bán theo quy luật cung cầu.</p> <p>Cũng theo ông Hiệp, việc Luật Kinh doanh bất động sản có hiệu lực từ 1/7/2015, với hàng loạt quy định sẽ làm chi phí đầu tư của doanh nghiệp tăng lên, khiến giá nhà đất còn tiếp tục tăng trong thời gian tới.</p> <p>Đại diện đơn vị phân phối Đất Xanh Miền Bắc, ông Vũ Cương Quyết thì phủ định việc thị trường có “liên minh” thổi giá nhà đất. Bởi theo ông Quyết, các đơn vị phân phối như Đất Xanh Miền Bắc chỉ có chức năng bán hàng hưởng hoa hồng, nếu tăng giá bán, việc bán hàng sẽ khó hơn, nên đơn vị phân phối không dại gì “bắt tay” chủ đầu tư tăng giá bán.</p> <p>Tuy nhiên, đại diện một đơn vị phân phối khác là ông Ngô Thành Long, Phó giám đốc Sàn giao dịch Hải Phát lại khẳng định: <em>“Phần lớn sàn giao dịch hiện nay muốn có sản phẩm phân phối, đều phải trở thành đơn vị thứ cấp tại một dự án cụ thể. Và để có lợi nhuận, các đơn vị này bắt buộc phải làm thị trường và tìm cách đẩy giá bán sản phẩm lên cao. Song giá cao đến đâu còn phụ thuộc vào nhu cầu thị trường”.</em></p> <p>Ngoài ra, ông Long cho biết thêm, hiện thị trường vẫn đang hồi phục, nên xu hướng tăng giá của thị trường căn hộ sẽ tiếp tục diễn ra, nhất là tại các dự án nội đô.</p> <p>Với việc các chủ đầu tư và phần lớn đơn vị phân phối đều tin thị trường vẫn đang trong xu hướng hồi phục và tìm cách đẩy giá bán, khiến “bong bóng” bất động sản tại Hà Nội không còn là nguy cơ nữa, mà đã và đang dần hiện hữu ngày một rõ ràng hơn. Nhưng như thông lệ, khi sự hưng phấn lên cao, mọi cảnh báo sẽ thường vô hiệu. </p> </div> <br /> <p><span class="VB_noi_dung"><br /></span></p> <div class="vivamain199" align="right"><span class="VB_noi_dung">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></span></div>', 6, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150704-doanh-nghiep-dia-oc-noi-gi-ve-lien-minh-thoi-gia-nha-dat-1.jpg', 0, '2015-07-04 08:20:32', 'đơn vị phân phối,việc tăng giá,chủ đầu tư,bất động sản,tại Hà Nội,giá bán,dự án,doanh nghiệp,căn hộ,nhà đất,liên minh,thổi,trung,năm', 'Đất tại một số khu vực, nhà tại một số dự án tại Hà Nội bị “thổi giá”. Đồn đoán này có cơ sở bởi nhiều dữ kiện khó lý giải, nhưng trong mắt nhiều doanh nghiệp địa ốc, nói “thổi giá” là chưa hẳn!', '2015-07-07 13:44:38', '2015-07-07 14:04:50', 1, 'http://vietbao.vn/Nha-dat/Doanh-nghiep-dia-oc-noi-gi-ve-lien-minh-thoi-gia-nha-dat/199125083/507/'),
(18, '“Soi” lợi nhuận từ các kênh đầu tư bất động sản', 'Soi-loi-nhuan-tu-cac-kenh-dau-tu-bat-dong-san', 'Mua căn hộ rồi cho thuê lại đang là kênh đầu tư khá phổ biến trên thị trường. Phần lớn các nhà đầu tư mua căn hộ với mục đích cho người nước ngoài thuê lại vì có thể cho thuê với giá cao.', '<p><span class="VB_gioi_thieu">Mua căn hộ rồi cho thuê lại đang là kênh đầu tư khá phổ biến trên thị trường. Phần lớn các nhà đầu tư mua căn hộ với mục đích cho người nước ngoài thuê lại vì có thể cho thuê với giá cao. </span></p> <p><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150706-soi-loi-nhuan-tu-cac-kenh-dau-tu-bat-dong-san-1.jpg" alt="�SSoi� loi nhuan tu cac kenh dau tu bat dong san" width="500" height="333" /></p> <p><span class="VB_noi_dung">Rất nhiều nhà đầu tư đã kiếm đậm từ “lướt sóng” căn hộ thời gian vừa qua khi thị trường bất động sản phục hồi và bùng nổ. Tuy nhiên, tâm lý đầu tư cũng đang dần thay đổi. Mặc dù “lướt sóng” sẽ vẫn là phương thức của những nhà đầu tư mạo hiểm kiếm lời nhanh nhưng ngày càng có nhiều nhà đầu tư chú ý đến khả năng mang lại doanh thu ổn định của bất động sản.</span></p> <p><span class="VB_noi_dung">Không chỉ tạo thu nhập thường xuyên mà những bất động sản mang lại lợi nhuận lớn từ cho thuê sẽ ngày càng khẳng định được giá trị. Trong đó, mua lại căn hộ, văn phòng, bất động sản nghỉ dưỡng để cho thuê hiện được coi là những kênh đầu tư khá tốt và bền vững. </span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Kênh đầu tư ưa thích</span></span></p> <p>Mua căn hộ rồi cho thuê lại đang là kênh đầu tư khá phổ biến trên thị trường. Phần lớn các nhà đầu tư mua căn hộ với mục đích cho người nước ngoài thuê lại vì có thể cho thuê với giá cao.</p> <p>Thực tế, một số dự án căn hộ cao cấp cho thuê ở TP. Hồ Chí Minh có mức sinh lời khá tốt. Khảo sát qua một số dự án ở khu vực có nhiều người nước ngoài sinh sống ở TP. Hồ Chí Minh như Xi Riverview, The Vista và Sunrise City của công ty cung cấp dịch vụ bất động sản CBRE cho thấy, những dự án này có thể mang lại tỷ suất sinh lời ngang bằng hoặc nhỉnh hơn lãi suất tiền gửi ngân hàng. Cụ thể, các dự án căn hộ ở quận 2 luôn duy trì tỷ suất sinh lời trên 8% trong suốt 3 năm qua, còn khu vực Nam Sài Gòn đem lại lợi nhuận khoảng 7,5-7,8%.</p> <p>Tại Hà Nội, thị trường căn hộ cho thuê không lớn bằng TP. Hồ Chí Minh và tỷ suất sinh lời cũng thấp hơn. Các dự án căn hộ cao cấp cho thuê tốt nhất hiện nay ở Thủ đô cũng chỉ mang lại tỷ suất lợi nhuận khoảng 6-7%/năm, còn đa phần ở mức 4-5%. Những dự án có giá cho thuê tốt nhất, dễ cho thuê nhất là Indochina Plaza Hanoi, Richland Southern, The Manor với giá cho thuê căn hộ 2 phòng ngủ dao động từ 1.000-1.500USD/tháng. </p> <p><span class="VB_tieu_de_phu">Kênh đầu tư mới nổi</span></p> <p>Theo nhận định của CBRE, xu hướng cho thuê dài hạn hoặc bán sàn văn phòng đang trở nên phổ biến hơn. Thậm chí, các tòa nhà chưa hoàn thiện cũng đã tính đến phương án bán văn phòng. Loại hình kinh doanh này được CBRE đánh giá là sẽ trở thành một kênh đầu tư mới khi các chủ đầu tư luôn mong muốn đa dạng hóa các loại hình đầu tư tài sản của mình.</p> <p>Đón đầu xu hướng này, IDJ Investment đang chào bán những lô văn phòng tại tòa nhà Charmvit Tower tại 117 Trần Duy Hưng, Hà Nội với giá tương đương giá căn hộ cao cấp là 42 triệu đồng/m2 (chưa VAT) trong thời gian 42 năm. Đây có thể coi là cơ hội hiếm hoi để mua được sàn văn phòng cho thuê hạng A ở Hà Nội, vì từ trước đến nay, các chủ tòa nhà chỉ muốn giữ lại cho thuê mà không muốn bán.</p> <p>Nhờ vào phương thức này, các doanh nghiệp có thể mua văn phòng làm trụ sở. Mặc dù phải chi tiền một lần, nhưng bù lại, lợi ích lại rất lớn vì hàng năm, doanh nghiệp không phải lo chuyển văn phòng, cũng không phải lo trả tiền hàng tháng. Hơn nữa, nếu tính về chi phí thì mua văn phòng làm trụ sở rẻ hơn nhiều so với đi thuê.</p> <p>Nhưng với nhiều nhà đầu tư, đây cũng là cơ hội không thể bỏ lỡ, vì tỷ suất sinh lời từ mua văn phòng cho thuê lại đang rất hấp dẫn. Theo phương án kinh doanh của IDJ Investment, những nhà đầu tư mua văn phòng với diện tích lớn hơn 100m2, nếu không trực tiếp cho thuê thì có thể cho IDJ Investment thuê lại, và đơn vị này cam kết sẽ trả mức lợi tức lên đến 10%/năm trong vòng 10 năm đầu tiên. So với lợi tức từ cho thuê nhà ở, thì mức lợi tức IDJ Investment cam kết hấp dẫn hơn nhiều. Đồng thời, đây cũng là mức cam kết lợi tức cao nhất từ phía các doanh nghiệp bất động sản trong các hình thức đầu tư trên thị trường hiện nay.</p> <p>Vì thế, ngay sau khi IDJ Investment chào bán văn phòng Charmvit, một số nhà đầu tư đã nhanh chân xuống tiền để chớp lấy cơ hội hiếm hoi này. </p> <p>Kết hợp đầu tư và nghỉ dưỡng</p> <p>Một kênh đầu tư đang dần lấy lại sự hấp dẫn là mua bất động sản nghỉ dưỡng. Theo đó, các nhà đầu tư có thể mua căn hộ, biệt thự trong các khu nghỉ dưỡng để đi nghỉ cùng gia đình và bạn bè hàng năm. Thời gian còn lại, chủ sở hữu có thể tự cho khách du lịch thuê, hoặc kết hợp với công ty quản lý khách sạn cho thuê lại và phân chia lợi nhuận. Thu nhập tăng lên, tầng lớp trung và thượng lưu ngày càng tăng, là những nhân tố kích thích người Việt đi du lịch và mua bất động sản nghỉ dưỡng.</p> <p>Tuy nhiên, mức giá của loại bất động sản này không hề rẻ, trung bình từ 3-5 tỷ đồng/căn hộ và trên 10 tỷ đồng/biệt thự.</p> <p>Nếu kết hợp với công ty quản lý khách sạn cho thuê bất động sản và phân chia lợi nhuận, thì tỷ suất sinh lời của loại hình bất động sản nghỉ dưỡng khoảng 4-5% năm. Tuy nhiên, một số doanh nghiệp có năng lực trong lĩnh vực kinh doanh nghỉ dưỡng như Vingroup đã mạnh dạn cam kết mức lợi tức lên tới 8%/năm trong vòng 10 năm đầu tiên. Đây cũng là mức cam kết lợi tức cao nhất trong lĩnh vực bất động sản nghỉ dưỡng.</p> <div class="vivamain199" align="right">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></div>', 6, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150706-soi-loi-nhuan-tu-cac-kenh-dau-tu-bat-dong-san-1.jpg', 0, '2015-07-06 13:23:20', 'IDJ Investment,Hồ Chí Minh,đầu tư bất động sản,nhà đầu tư,cho thuê lại,tỷ suất sinh,người nước ngoài,căn hộ,văn phòng,có thể,lợi nhuận,thị trường,dự án,mua,kênh', 'Mua căn hộ rồi cho thuê lại đang là kênh đầu tư khá phổ biến trên thị trường. Phần lớn các nhà đầu tư mua căn hộ với mục đích cho người nước ngoài thuê lại vì có thể cho thuê với giá cao.', '2015-07-07 13:44:38', '2015-07-07 14:04:50', 1, 'http://vietbao.vn/Nha-dat/Soi-loi-nhuan-tu-cac-kenh-dau-tu-bat-dong-san/199125144/507/'),
(19, 'Doanh nghiệp địa ốc chạy đua đón chính sách mới', 'Doanh-nghiep-dia-oc-chay-dua-don-chinh-sach-moi', 'Thị trường bất động sản TP. HCM quý II đã khép lại với một kỷ lục mới về thanh khoản được thiết lập. Phân tích của các đơn vị nghiên cứu thị trường cho thấy, thị trường càng lúc càng thể hiện được xu hướng phát triển bền vững. Xu hướng này sẽ tiếp tục duy', '<div><span class="VB_gioi_thieu">Thị trường bất động sản TP. HCM quý II đã khép lại với một kỷ lục mới về thanh khoản được thiết lập. Phân tích của các đơn vị nghiên cứu thị trường cho thấy, thị trường càng lúc càng thể hiện được xu hướng phát triển bền vững. Xu hướng này sẽ tiếp tục duy trì và phát triển tích cực hơn trong thời gian tới nhờ nhiều chính sách mới đi vào cuộc sống.</span> <p align="center"><span class="VB_noi_dung"><img src="http://a9.vietbao.vn/images/vn999/199/2015/07/20150707-doanh-nghiep-dia-oc-chay-dua-don-chinh-sach-moi-1.jpg" alt="Doanh nghiep dia oc chay dua don chinh sach moi" height="257" width="449" /></span></p> <p align="center"><span class="VB_chu_thich_hinh">Các doanh nghiệp địa ốc kỳ vọng các chính sách mới sẽ tác động tích cực đến thị trường bất động sản Việt Nam trong thời gian tới - Ảnh: Lê Toàn Ảnh: Lê Toàn</span></p> <p><span class="VB_noi_dung"><span class="VB_tieu_de_phu">Căn hộ cao cấp “lên ngôi”</span></span></p> <p>Báo cáo về thị trường bất động sản TP. HCM quý II do Công ty CBRE Việt Nam công bố cuối tuần qua cho biết, nếu như ở đầu năm vẫn còn những hoài nghi về việc phục hồi của thị trường, thì hiện nay, bức tranh toàn cảnh thị trường bất động sản đã trở nên rõ ràng hơn, đặc biệt là với thị trường căn hộ đã thể hiện sự phục hồi một cách bền vững.</p> <p>Điều này đã được chứng minh với lượng tiêu thụ căn hộ trong quý II/2015 đạt hơn 10.000 căn, gồm cả dự án mới và đã chào bán trước đó, đánh dấu một kỷ lục mới về thanh khoản theo quý từ nhiều năm qua.</p> <p>Theo ông Marc Townsend, Tổng giám đốc điều hành của CBRE Việt Nam, có một thay đổi thú vị là trong giai đoạn 2012-2013, tỷ lệ giao dịch thành công chủ yếu từ phân khúc bình dân, thì trong thời gian gần đây đã chuyển dịch sang phân khúc cao cấp. Trong quý II/2015, phân khúc cao cấp ghi nhận mức tiêu thụ khoảng 5.800 căn hộ, trong khi chỉ có khoảng 2.800 căn hộ được bán trong phân khúc bình dân.</p> <p>Cũng theo CBRE, quý II/2015 được ghi nhận là quý có số căn hộ cao cấp mở bán theo quý đứng thứ nhì trong lịch sử thị trường TP. HCM với 4.500 căn, đa số nằm ở khu Đông Sài Gòn, như Estella Heights (giai đoạn 2 của Dự án The Estella), Tháp 2 của Dự án The Sun Avenue, The Krista (giai đoạn 2 của Dự án PARC Spring), Vista Verde - Tòa tháp Lotus, Gateway Thảo Điền, Khu căn hộ Sarimi thuộc Khu đô thị Sala Thủ Thiêm và các tòa Landmark 1, 2, 4, 5 thuộc Dự án Vinhomes Central Park.</p> <p>Không chỉ dừng lại ở số căn hộ chào bán mới, hầu hết các dự án cao cấp đã tăng giá bán trong giai đoạn/lần mở bán sau, giá sơ cấp trên thị trường căn hộ cao cấp tăng nhẹ 3,2% so với quý trước, đạt 1.781 USD/m2.</p> <p><em>“Với mức tiêu thụ ấn tượng như hiện nay và mức giá được cải thiện trong nửa đầu năm 2015, chúng tôi có thể khẳng định, giai đoạn khó khăn đã qua. Thị trường khó mà quay lại thời kỳ bong bóng như năm 2007, khi mà người mua phải xếp hàng để mua căn hộ và dễ dàng bán lại trong một đêm. Tuy nhiên, hiện tại, thị trường đang phát triển bền vững và người mua ngày càng vững tin hơn vào thị trường, điều mà bất động sản Việt Nam đã không có được trong chu kỳ phát triển trước. Thị trường cổ phiếu, giá vàng, tỷ giá, lãi suất... đã tạo điều kiện cho thị trường bất động sản lần đầu tiên trở thành một sân chơi hấp dẫn trong 7 năm qua, thu hút nhiều gia đình Việt Nam đến mua căn hộ, đặc biệt là những căn diện tích nhỏ và các sản phẩm mới”</em>, ông Marc Townsend nhận định. </p> <p><span class="VB_tieu_de_phu">Chạy đua đón chính sách</span></p> <p>Theo phân tích của giới kinh doanh địa ốc, dù thị trường quý II có một sự đột phá và là cơ sở để tin tưởng thị trường có khả năng sẽ còn tiếp tục phát triển mạnh, nhưng tác động của mốc sự kiện ngày 1/7/2015, thời điểm 2 sắc luật là Luật Nhà ở sửa đổi và Luật Kinh doanh bất động sản sửa đổi có hiệu lực vẫn còn phải chờ đợi.</p> <p>Theo phân tích của CBRE, tác động của 2 sắc luật trên sẽ đưa bất động sản Việt Nam lên một bước mới trong việc tiếp cận với nguồn đầu tư nước ngoài. Không chỉ mở rộng cho thị trường bất động sản, Chính phủ cũng vừa ký ban hành Nghị định 60, nới room cho các nhà đầu tư nước ngoài trong các công ty đại chúng thuộc các ngành nghề mà pháp luật không cấm, hoặc kinh doanh có điều kiện, hoặc theo các cam kết mở cửa. Tuy nhiên, các chính sách này sẽ không có tác động tức thì, bởi nhà đầu tư nước ngoài luôn theo dõi thị trường một cách cẩn thận.</p> <p>Người mua nước ngoài sẽ quan sát và tiến hành giao dịch chỉ khi thị trường thực sự hồi phục. Động thái “chờ và xem” sẽ phổ biến trước khi có một quyết định mua bán cụ thể nào.</p> <p><em>“Các sắc luật mới bắt đầu có hiệu lực, nhất là quy định mở rộng sở hữu nhà ở dành cho người nước ngoài là thông tin tốt cho thị trường. Trong suốt 15 năm qua, nhà đầu tư nước ngoài vẫn thường phàn nàn về sự không công bằng trong thị trường bất động sản Việt Nam, còn bây giờ, với sự thay đổi này, chúng ta sẽ xem liệu họ có thực sự muốn nắm bắt cơ hội, có thực sự vui với luật mới, hay vẫn băn khoăn về cách chuyển tiền vào và ra. Để thuyết phục nhóm đối tượng này, ngoài luật pháp rõ ràng và hệ thống ngân hàng tốt hơn, yếu tố chính cần phải tập trung là con người, sản phẩm và thời điểm bán hàng”</em>, ông Marc Townsend khuyến nghị.</p> <p>Dù giới chuyên môn vẫn còn cái nhìn thận trọng về xu hướng thị trường trong quý III và thời gian tới, cũng như khả năng thực thi các sắc luật mới, nhưng theo ghi nhận của chúng tôi, các doanh nghiệp đã có những bước chuẩn bị kỹ để thực thi và đón đầu chính sách, dù các văn bản hướng dẫn chưa được ban hành.</p> <p>Đơn cử, cuối tuần qua, nhiều doanh nghiệp đã bắt tay với các ngân hàng trong việc ký kết hợp đồng bảo lãnh nhà ở hình thành trong tương lai. Ngày 3/7, Hưng Thịnh Corp đã ký kết hợp tác bảo lãnh dự án với 5 ngân hàng là VietBank, SCB, Sacombank, VietinBank và BIDV nhằm cam kết bảo lãnh cho người mua nhà đối với các dự án do Hưng Thịnh đang triển khai.</p> <p>Ông Nguyễn Đình Trung, Chủ tịch HĐQT kiêm Tổng giám đốc Hưng Thịnh Corp cho biết, việc ký kết hợp tác bảo lãnh với ngân hàng không chỉ giúp đảm bảo tuyệt đối quyền lợi cho người mua nhà, mà còn góp phần tăng thêm niềm tin đối với khách hàng và các nhà đầu tư, đồng thời giúp thị trường bất động sản phát triển bền vững.</p> <p>Ngoài ra, việc các doanh nghiệp được ngân hàng ký kết bảo lãnh dự án còn giúp khẳng định thêm uy tín thương hiệu của doanh nghiệp trên thị trường bất động sản. Bởi khi quyết định bảo lãnh, ngân hàng đã phải thẩm định rất kỹ uy tín và năng lực của doanh nghiệp, cũng như tính thanh khoản các dự án của đơn vị mà mình bảo lãnh.</p> <p>Tương tự, cũng trong tuần qua, CTCP Đầu tư và kinh doanh nhà Khang Điền đã ký kết với Eximbank về việc hợp tác bảo lãnh, cam kết thực hiện bảo lãnh cho người mua nhà tại các dự án do Khang Điền làm chủ đầu tư. Trước đó, nhiều doanh nghiệp địa ốc khác như Novaland, Sacomreal… cũng đã chính thức bắt tay với các ngân hàng để bảo lãnh các dự án.</p> <p>Không chỉ chạy đua việc thực hiện các quy định mới, dịp này, nhiều doanh nghiệp địa ốc phía Nam cũng tung ra nhiều chương trình kinh doanh nhằm đón chính sách mới. Đơn cử, mới đây, Tập đoàn Novaland tung ra chương trình “100 căn hộ đầu tiên chào đón kiều bào và người nước ngoài” với những ưu đãi đặc biệt.</p> <p>Cụ thể, tặng gói cam kết cho thuê tương đương 8% trong năm đầu tiên và gói dịch vụ hỗ trợ quản lý trong giai đoạn hoàn thiện nội thất, đặc biệt, cam kết hoàn tiền mua nhà cộng lãi suất phát sinh nếu trong vòng 1 tháng từ thời điểm giao nhà nếu khách hàng chưa hài lòng về sản phẩm.</p> <p>Chương trình được bắt đầu từ ngày 1/7 đối với 100 căn hộ đầu tiên của 4 dự án do Novaland đầu tư và phát triển là Dự án The Sun Avenue (quận 2), The Botanica (quận Tân Bình), Lucky Palace (quận 6), Sunrise City View (quận 7).</p> <p>Theo ông Chaw Chee Fan, Tổng giám đốc CTCP Đầu tư Sài Gòn Thương Tín Tân Thắng, chủ đầu tư Dự án Celadoncity (quận Tân Phú), trong tuần này, Dự án Celadoncity Block C sẽ chính thức được chào bán ra thị trường với nhiều chính sách mới dành cho người nước ngoài.</p> <p><em>“Ngay trong tuần này, sẽ có một đoàn khách từ Malaysia đến tham quan và tìm hiểu Dự án Celadoncity”</em>, ông Chaw Chee Fan nói và cho biết, rất kỳ vọng vào các chính sách mới sẽ tác động mạnh đến thị trường bất động sản Việt Nam sắp tới.</p> </div> <br /> <p><span class="VB_noi_dung"><br /></span></p> <div class="vivamain199" align="right"><span class="VB_noi_dung">VietBao.vn <script language="JavaScript" type="text/javascript"> //<![CDATA[ showsource("199"); //]]> </script></span></div>', 6, 0, 'http://img.vietbao.vn/images/2000/vn999/199/2015/07/20150707-doanh-nghiep-dia-oc-chay-dua-don-chinh-sach-moi-1.jpg', 0, '2015-07-07 08:30:48', 'Việt Nam,Marc Townsend,thị trường bất động sản,nhà đầu tư nước ngoài,Phát triển bền vững,xu hướng phát triển,nghiên cứu thị trường,chính sách mới,trong thời gian,thời gian tới,tổng giám đốc,doanh nghiệp,căn hộ,bảo lãnh,địa ốc,quý', 'Thị trường bất động sản TP. HCM quý II đã khép lại với một kỷ lục mới về thanh khoản được thiết lập. Phân tích của các đơn vị nghiên cứu thị trường cho thấy, thị trường càng lúc càng thể hiện được xu hướng phát triển bền vững. Xu hướng này sẽ tiếp tục duy', '2015-07-07 13:44:38', '2015-07-07 14:04:50', 1, 'http://vietbao.vn/Nha-dat/Doanh-nghiep-dia-oc-chay-dua-don-chinh-sach-moi/199125232/507/');
INSERT INTO `tbl_news_content` (`id`, `title`, `alias`, `introtext`, `fulltext`, `catid`, `uid`, `thumbnail`, `ordering`, `created`, `metakey`, `metadesc`, `cdate`, `mdate`, `status`, `link_original`) VALUES
(20, 'Đình chỉ 4 nhân viên cây xăng gian lận', 'Dinh-chi-4-nhan-vien-cay-xang-gian-lan', '(VM)- Cửa hàng trưởng và 3 nhân viên trạm bơm xăng 342 Phạm Văn Đồng (Hà Nội) đã bị đình chỉ sau khi bị phát hiện có hành vi lận nghiêm trọng tại trạm bơm xăng này.', '<p><span class="VB_gioi_thieu">Cửa hàng trưởng và 3 nhân viên trạm bơm xăng 342 Phạm Văn Đồng (Hà Nội) đã bị đình chỉ sau khi bị phát hiện có hành vi lận nghiêm trọng tại trạm bơm xăng này.</span></p> <p><span id="advenueINTEXT"></span></p> <p><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><img class="decoded" alt="http://a8.vietbao.vn/images/vn888/hot/v2012/2131540748-1-613tinlinequan.png" src="http://a8.vietbao.vn/images/vn888/hot/v2012/2131540748-1-613tinlinequan.png" height="20" width="260" /></span></span></span></span></span></span></span></span></span></span></span></p> <span id="advenueINTEXT">>> <strong><a target="_blank" href="http://vietbao.vn/Video-clip/Ha-Noi-Vach-mat-thu-doan-moc-tui-tinh-vi-cua-nhan-vien-ban-xang/2131674584/601/">Hà Nội: Vạch mặt thủ đoạn "móc túi" tinh vi của nhân viên bán xăng</a></strong></span> <div id="vivamain65" class="vivamain65"> <p><span class="VB_noi_dung">Sáng qua, sau phản ánh của báo chí về hàng loạt hành vi “móc túi” khách hàng tại trạm bơm xăng 342 Phạm Văn Đồng, công ty CP vận tải và dịch vụ Petrolimex Hà Tây (PTS Hà Tây) đã có văn bản báo cáo nhanh về vụ việc. Theo đó, đây là cửa hàng PTS Hà Tây liên kết với CTCP Xăng dầu Nam Anh, chính thức hoạt động từ tháng 3/2012 tới nay.</span></p> <p><span class="VB_noi_dung">Cửa hàng này có 1 cửa hàng trưởng (Đoàn Hải Yến), 1 nhân viên nghiệp vụ và 6 nhân viên bán hàng. Theo điều tra ban đầu của công ty PTS Hà Tây, ca bán hàng có hành vi gian lận bị phát hiện gồm 3 nhân viên Ninh Thị Mai, Nguyễn Văn Giáp và Nguyễn Thanh Tùng và hành vi gian lận có thể xảy ra vào buổi chiều và tối các ngày 28 và 30/7/2013. <font face="Arial" size="2"><br /></font></span></p> <table align="center" border="0" cellpadding="8" cellspacing="0" width="60%"> <tbody> <tr> <td bgcolor="#FFFFFF" valign="top"><img alt="Ảnh minh họa" src="http://a9.vietbao.vn/images/vn965/o-to/66487808-vnm_2013_4179204.jpg" border="0" /></td> </tr> <tr> <td bgcolor="#FFFFFF" valign="top"> <p align="center"><span class="VB_chu_thich_hinh">Trạm xăng 342 Phạm Văn Đồng bị phát hiện gian lận. ảnh Dân trí</span></p> </td> </tr> </tbody> </table> <p><span class="VB_noi_dung">Theo ông Đàm Quang Dũng, Chủ tịch HĐQT PTS Hà Tây, ngay sau khi có thông tin, công ty đã tiến hành ra quyết định tạm đình chỉ cửa hàng trưởng và 3 nhân viên trong ca bán hàng có hành vi gian lận, triệu tập về công ty làm tường trình để có biện pháp xử lý tiếp theo. Công ty cũng tạm thời giao nhiệm vụ cho cán bộ khác để cửa hàng hoạt động bình thường…<br /> <br /> Theo Dân trí, bất kể là sáng hay chiều, những nhân viên ở trạm bơm xăng này phối hợp rất ăn ý với nhau để ăn bớt xăng của khách mua xăng một cách trắng trợn. Chỉ cần khách mua xăng không để ý đến đồng hồ bơm xăng, các nhân viên bán xăng tại đây thoắt trở thành các diễn viên thực thụ với màn ảo thuật bơm nối số và xóa số trên đồng hồ cột bơm xăng. Sau nhiều ngày theo dõi, có thể xác định hành vi gian lận tại cây xăng này diễn ra thường xuyên và rất chuyên nghiệp.</span></p> <div align="left"> <p><span class="VB_noi_dung"><span id="advenueINTEXT"></span></span></p> <div class="art-lead"> <p align="right"><span id="advenueINTEXT"><em>Viet Bao.vn (Theo VnMedia)</em><br /></span></p> <span id="advenueINTEXT"></span> <p><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><em><span class="VB_tieu_de_phu">TIN NỔI BẬT CHUYÊN MỤC <a target="_blank" href="http://vietbao.vn/Xa-hoi/">XÃ HỘI</a></span></em><br /></span></span></span></p> <p><span style="BACKGROUND: white; COLOR: #222222"><span id="advenueINTEXT"></span></span></p> <p><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span class="VB_noi_dung"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="ContentPlaceHolder1_lblContent"><span id="advenueINTEXT"><span class="VB_noi_dung"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"></span></span></span></span></span></span></span></span></span></span></span></span> <span id="ctl00_IDContent_BlogDetail1_ltrContent" class="bodyContent"></span> <span id="ctl00_IDContent_BlogDetail1_ltrContent" class="bodyContent"></span></span></span></span></span></p> <p><span id="advenueINTEXT"></span></p> <div id="pageContent" class="articleBody" style="width: 100%; padding-top: 10px;font-family:Arial;text-align:justify;"><span id="advenueINTEXT"></span> <div class="vivamain75"><span id="advenueINTEXT"></span> <p><span id="advenueINTEXT"><span id="ctl00_IDContent_BlogDetail1_ltrContent" class="bodyContent"><span id="advenueINTEXT"></span></span></span></p> <div class="art-lead"><span id="ctl00_IDContent_BlogDetail1_ltrContent" class="bodyContent"></span> <p align="left"><span id="ctl00_IDContent_BlogDetail1_ltrContent" class="bodyContent"><span id="advenueINTEXT"><span id="ContentPlaceHolder1_lblContent"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"></span></span></span></span></span></span></span></span></span></p> <p align="left"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="ContentPlaceHolder1_lblContent"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="ContentPlaceHolder1_lblContent"><span id="advenueINTEXT"><span id="advenueINTEXT"><span id="advenueINTEXT"></sp